import React, { useState } from 'react';
import { Event } from '../types';
import { format } from 'date-fns';
import { ChevronDownIcon, ChevronUpIcon } from '@heroicons/react/24/outline';

interface EventCardProps {
  event: Event;
  onRegister?: () => void;
  onWithdraw?: () => void;
  onCancel?: () => void;
  isRegistered?: boolean;
  isCreator?: boolean;
}

const EventCard: React.FC<EventCardProps> = ({ event, onRegister, onWithdraw, onCancel, isRegistered, isCreator }) => {
  const [isExpanded, setIsExpanded] = useState(false);

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <div className="bg-white shadow rounded-lg p-4 mb-4">
      <div className="flex justify-between items-start">
        <div>
          <h3 className="text-lg font-medium text-gray-900">{event.court_location}</h3>
          <p className="text-sm text-gray-500">
            {format(new Date(event.event_date), 'MMMM d, yyyy')} at{' '}
            {format(new Date(event.event_time), 'h:mm a')}
          </p>
          <div className="mt-2">
            <p className="text-sm text-gray-600">
              {isExpanded ? event.court_location : event.court_location.split(',').slice(0, 2).join(',')}
            </p>
            {event.court_location.split(',').length > 2 && (
              <button
                onClick={toggleExpand}
                className="text-sm text-blue-500 flex items-center mt-1"
              >
                {isExpanded ? (
                  <>
                    Show Less <ChevronUpIcon className="h-4 w-4 ml-1" />
                  </>
                ) : (
                  <>
                    Show More <ChevronDownIcon className="h-4 w-4 ml-1" />
                  </>
                )}
              </button>
            )}
          </div>
        </div>
        <div className="flex flex-col gap-2">
          {onRegister && !isRegistered && !isCreator && event.max_participants !== null && event.registrations && event.registrations.length >= event.max_participants && (
            <button
              className="bg-gray-400 text-white px-4 py-2 rounded opacity-60 cursor-not-allowed pointer-events-none border border-gray-300"
              disabled
            >
              Event Full
            </button>
          )}
          {onRegister && !isRegistered && !isCreator && (event.max_participants === null || !event.registrations || event.registrations.length < event.max_participants) && (
            <button
              onClick={onRegister}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
            >
              Register
            </button>
          )}
          {onWithdraw && isRegistered && !isCreator && (
            <button
              onClick={onWithdraw}
              className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
            >
              Withdraw
            </button>
          )}
          {onCancel && isCreator && (
            <button
              onClick={onCancel}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
            >
              Cancel Event
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default EventCard;
