import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getEvents, registerForEvent, cancelEvent } from '../services/api';
import { Event } from '../types';
import { format, isThisWeek, isThisMonth, addWeeks, startOfWeek, endOfWeek, isPast, parseISO } from 'date-fns';
import { useAuth } from '../contexts/AuthContext';
import LocationMap from '../components/LocationMap';

const Dashboard: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();
  const [toast, setToast] = useState<{ message: string; type: 'success' | 'error' } | null>(null);
  const [selectedEvent, setSelectedEvent] = useState<Event | null>(null);
  const [expandedSections, setExpandedSections] = useState({
    upcoming: true,
    past: false
  });
  const [searchCity, setSearchCity] = useState('');
  const [mileRange, setMileRange] = useState(20);
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [selectedLocation, setSelectedLocation] = useState<{ lat: number; lon: number; display_name: string } | null>(null);

  useEffect(() => {
    fetchEvents();
  }, []);

  // Auto-hide toast after 3 seconds
  useEffect(() => {
    if (toast) {
      const timer = setTimeout(() => {
        setToast(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [toast]);

  const fetchEvents = async () => {
    try {
      const data = await getEvents();
      // Sort events by date
      const sortedEvents = data.sort((a, b) => 
        new Date(a.event_date).getTime() - new Date(b.event_date).getTime()
      );
      setEvents(sortedEvents);
      setError(null);
    } catch (err: any) {
      console.error('Failed to load events:', err);
      let errorMsg = 'Failed to load events';
      if (err.detail) {
        errorMsg = typeof err.detail === 'string' ? err.detail : JSON.stringify(err.detail);
      } else if (err.message) {
        errorMsg = err.message;
      }
      setError(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (eventId: number) => {
    try {
      await registerForEvent(eventId);
      setToast({ message: 'Successfully registered for event', type: 'success' });
      fetchEvents(); // Refresh events to update the UI
    } catch (error: any) {
      console.error('Failed to register for event:', error);
      setToast({ 
        message: error.response?.data?.detail || error.detail || 'Failed to register for event', 
        type: 'error' 
      });
    }
  };

  const handleWithdraw = async (eventId: number) => {
    try {
      await registerForEvent(eventId, true);
      setToast({ message: 'Successfully withdrew from event', type: 'success' });
      fetchEvents(); // Refresh events to update the UI
    } catch (error: any) {
      console.error('Failed to withdraw from event:', error);
      setToast({ 
        message: error.response?.data?.detail || error.detail || 'Failed to withdraw from event', 
        type: 'error' 
      });
    }
  };

  const handleCancelEvent = async (eventId: number) => {
    try {
      console.log('Attempting to cancel event:', eventId);
      await cancelEvent(eventId);
      console.log('Event cancelled successfully');
      setToast({ message: 'Successfully cancelled event', type: 'success' });
      fetchEvents(); // Refresh events to update the UI
    } catch (error: any) {
      console.error('Failed to cancel event:', error);
      console.error('Error details:', {
        status: error.response?.status,
        data: error.response?.data,
        message: error.message,
        detail: error.detail
      });
      setToast({ 
        message: error.response?.data?.detail || error.detail || 'Failed to cancel event', 
        type: 'error' 
      });
    }
  };

  const toggleSection = (section: keyof typeof expandedSections) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const categorizeEvents = () => {
    const now = new Date();
    return {
      upcoming: events.filter(event => new Date(event.event_date) >= now && !event.is_cancelled),
      past: events.filter(event => new Date(event.event_date) < now && !event.is_cancelled)
    };
  };

  const renderEventCard = (event: Event) => {
    const totalSpots = event.max_participants || null;
    const currentRegistrations = event.registrations?.length || 0;
    const availableSpots = totalSpots ? totalSpots - currentRegistrations : null;

    // Check if user is organizer or registered
    const isOrganizer = event.organizer_id === user?.id;
    const isRegistered = event.registrations?.some(reg => reg.user?.id === user?.id) || false;

    return (
      <div
        key={event.id}
        className={`bg-white overflow-hidden shadow rounded-lg hover:shadow-md transition-shadow duration-200 ${
          isOrganizer ? 'border-2 border-[#C4E538]' : ''
        }`}
      >
        <div className="px-4 py-5 sm:p-6">
          <h3 className="text-lg font-medium text-gray-900">
            Tennis Match at {event.court_location}
            {isOrganizer && (
              <span className="ml-2 inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-[#C4E538] text-gray-900">
                Your Event
              </span>
            )}
          </h3>
          <div className="mt-2 text-sm text-gray-500">
            <p>
              Date:{' '}
              {format(new Date(event.event_date), 'MMMM d, yyyy')}
            </p>
            <p>
              Time:{' '}
              {format(new Date(event.event_time), 'h:mm a')}
            </p>
            {totalSpots !== null ? (
              <p>
                Participants: {currentRegistrations}/{totalSpots}
              </p>
            ) : (
              <p>
                Participants: {currentRegistrations} (open registration)
              </p>
            )}
          </div>

          <div className="mt-6 space-y-3">
            <button
              onClick={() => setSelectedEvent(event)}
              className="w-full inline-flex justify-center items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538]"
            >
              See Event Details
            </button>
            <div className="mt-4 flex justify-center space-x-2">
              {event.organizer_id === user?.id ? (
                <>
                  {isRegistered ? (
                    <button
                      onClick={() => handleWithdraw(event.id)}
                      className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-yellow-700 bg-yellow-100 hover:bg-yellow-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-yellow-500"
                    >
                      Withdraw
                    </button>
                  ) : (
                    <button
                      onClick={() => handleRegister(event.id)}
                      className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-gray-900 bg-[#C4E538] hover:bg-[#B4D532] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538]"
                    >
                      Register
                    </button>
                  )}
                  <button
                    onClick={() => handleCancelEvent(event.id)}
                    className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-red-700 bg-red-100 hover:bg-red-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                  >
                    Cancel Event
                  </button>
                </>
              ) : (
                <button
                  onClick={() => isRegistered ? handleWithdraw(event.id) : handleRegister(event.id)}
                  disabled={event.is_cancelled || (totalSpots !== null && (availableSpots || 0) <= 0)}
                  className={`inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md ${
                    event.is_cancelled
                      ? 'text-gray-400 bg-gray-100 cursor-not-allowed'
                      : isRegistered
                      ? 'text-yellow-700 bg-yellow-100 hover:bg-yellow-200'
                      : totalSpots !== null && (availableSpots || 0) <= 0
                      ? 'bg-gray-400 text-white opacity-60 cursor-not-allowed pointer-events-none border border-gray-300'
                      : 'text-gray-900 bg-[#C4E538] hover:bg-[#B4D532]'
                  } focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-yellow-500`}
                >
                  {event.is_cancelled
                    ? 'Event Cancelled'
                    : isRegistered
                    ? 'Withdraw'
                    : totalSpots !== null && (availableSpots || 0) <= 0
                    ? 'Event Full'
                    : 'Register'}
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  };

  const renderEventSection = (title: string, events: Event[], sectionKey: keyof typeof expandedSections) => (
    <div className="mb-8">
      <button
        onClick={() => toggleSection(sectionKey)}
        className="w-full flex justify-between items-center text-2xl font-bold text-gray-900 mb-4 hover:text-[#C4E538] transition-colors"
      >
        <span>{title}</span>
        <span className="text-lg">
          {expandedSections[sectionKey] ? '▼' : '▶'}
        </span>
      </button>
      {expandedSections[sectionKey] && events.length > 0 ? (
        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {events.map(renderEventCard)}
        </div>
      ) : (
        <p className="text-gray-500 text-center py-4">
          {expandedSections[sectionKey] ? 'No events in this period' : ''}
        </p>
      )}
    </div>
  );

  const { upcoming, past } = categorizeEvents();
  const hasAnyEvents = upcoming.length > 0 || past.length > 0;

  const handleSearch = async () => {
    if (!searchCity) return;
    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchCity)}&type=city&limit=5`
      );
      const data = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.error('Error fetching location data:', error);
    }
  };

  const handleLocationSelect = (location: any) => {
    setSelectedLocation({
      lat: parseFloat(location.lat),
      lon: parseFloat(location.lon),
      display_name: location.display_name.split(',')[0]
    });
    setSearchCity(location.display_name.split(',')[0]);
    setSearchResults([]);
  };

  const handleCityChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchCity(e.target.value);
    if (e.target.value) {
      handleSearch();
    } else {
      setSearchResults([]);
    }
  };

  const calculateDistance = (lat1: number, lon1: number, lat2: number, lon2: number) => {
    const R = 3959; // Earth's radius in miles
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const filteredEvents = events.filter(event => {
    if (!selectedLocation) return false;

    // Only include events that have valid coordinates
    if (!event.latitude || !event.longitude) return false;

    const distance = calculateDistance(
      selectedLocation.lat,
      selectedLocation.lon,
      event.latitude,
      event.longitude
    );

    return distance <= mileRange;
  });

  // Add console logs for debugging
  console.log('Selected Location:', selectedLocation);
  console.log('Events:', events);
  console.log('Filtered Events:', filteredEvents);

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-extrabold text-gray-900 sm:text-5xl sm:tracking-tight lg:text-6xl">
            Find Events
          </h1>
          <p className="mt-5 max-w-xl mx-auto text-xl text-gray-500">
            Discover tennis events near you
          </p>
        </div>

        {/* Search Bar and Slider */}
        <div className="mb-8">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="relative flex-1">
              <input
                type="text"
                placeholder="Enter city name"
                value={searchCity}
                onChange={handleCityChange}
                onBlur={() => setTimeout(() => setSearchResults([]), 200)}
                className="px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-[#C4E538] w-full"
              />
              {searchResults.length > 0 && (
                <div className="absolute z-10 w-full bg-white border border-gray-300 rounded-md shadow-lg mt-1">
                  {searchResults.map((result, index) => (
                    <div
                      key={index}
                      className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
                      onClick={() => handleLocationSelect(result)}
                    >
                      {result.display_name.split(',')[0]}
                    </div>
                  ))}
                </div>
              )}
            </div>
            <div className="flex items-center gap-2 min-w-[200px]">
              <input
                type="range"
                min="1"
                max="100"
                value={mileRange}
                onChange={(e) => setMileRange(Number(e.target.value))}
                className="w-full"
              />
              <span className="text-sm text-gray-600 whitespace-nowrap">{mileRange} miles</span>
            </div>
          </div>
        </div>

        {/* Events Grid */}
        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {filteredEvents.map((event) => (
            <EventCard
              key={event.id}
              event={event}
              onRegister={() => handleRegister(event.id)}
              onUnregister={() => handleWithdraw(event.id)}
              isRegistered={event.registrations?.some(reg => reg.user?.id === user?.id) || false}
              isOrganizer={event.organizer_id === user?.id}
              onEdit={() => navigate(`/manage-events/${event.id}`)}
            />
          ))}
        </div>
        {filteredEvents.length === 0 && selectedLocation && (
          <div className="text-center mt-8">
            <p className="text-gray-500">No events found within {mileRange} miles of {searchCity}</p>
          </div>
        )}

        {/* Event Details Modal */}
        {selectedEvent && (
          <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
              <div className="p-6">
                <div className="flex justify-between items-start">
                  <h2 className="text-2xl font-bold text-gray-900">
                    Event Details
                  </h2>
                  <button
                    onClick={() => setSelectedEvent(null)}
                    className="text-gray-400 hover:text-gray-500"
                  >
                    <span className="sr-only">Close</span>
                    <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>

                <div className="mt-4 space-y-4">
                  <div>
                    <h3 className="text-lg font-medium text-gray-900">Location</h3>
                    <p className="mt-1 text-gray-500">{selectedEvent.court_location}</p>
                    <div className="mt-2">
                      <LocationMap 
                        location={selectedEvent.court_location} 
                        latitude={selectedEvent.latitude}
                        longitude={selectedEvent.longitude}
                      />
                    </div>
                  </div>

                  <div>
                    <h3 className="text-lg font-medium text-gray-900">Date & Time</h3>
                    <p className="mt-1 text-gray-500">
                      {format(new Date(selectedEvent.event_date), 'MMMM d, yyyy')} at{' '}
                      {format(new Date(selectedEvent.event_time), 'h:mm a')}
                    </p>
                  </div>

                  {selectedEvent.description && (
                    <div>
                      <h3 className="text-lg font-medium text-gray-900">Description</h3>
                      <p className="mt-1 text-gray-500">{selectedEvent.description}</p>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
