import React, { useState, useEffect } from 'react';
import { getEvents, cancelEvent } from '../services/api';
import { Event } from '../types';

const ManageEvents: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const eventsData = await getEvents();
      console.log('Event data:', eventsData); // Debug log
      setEvents(eventsData);
    } catch (err) {
      setError('Failed to load events');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelEvent = async (eventId: number) => {
    try {
      await cancelEvent(eventId);
      setEvents(prevEvents => prevEvents.filter(event => event.id !== eventId));
      setSuccess('Event cancelled successfully');
    } catch (err) {
      setError('Failed to cancel event');
    }
  };

  const formatDateTime = (date: string, time: string) => {
    // Parse the date string (format: YYYY-MM-DD)
    const [year, month, day] = date.split('-').map(Number);
    
    // Parse the time string (format: HH:MM:SS)
    const [hours, minutes] = time.split(':').map(Number);
    
    // Create a new Date object with the parsed values
    const dateTime = new Date(year, month - 1, day, hours, minutes);
    
    // Format the date and time
    return dateTime.toLocaleString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true
    });
  };

  if (loading) {
    return <div className="flex justify-center items-center min-h-screen">Loading...</div>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-8">Manage Events</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}
      {success && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
          {success}
        </div>
      )}

      {events.length === 0 ? (
        <p className="text-gray-500 text-center">You haven't organized any events yet.</p>
      ) : (
        <div className="space-y-4">
          {events.map(event => (
            <div key={event.id} className="bg-white shadow rounded-lg p-6">
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="text-xl font-semibold">{event.court_location}</h3>
                  <p className="text-gray-600 mt-2">
                    {formatDateTime(event.event_date, event.event_time)}
                  </p>
                  <p className="text-sm text-gray-500 mt-1">
                    {event.registrations.length} participants
                    {event.max_participants && ` / ${event.max_participants} max`}
                  </p>
                </div>
                {!event.is_cancelled && (
                  <button
                    onClick={() => handleCancelEvent(event.id)}
                    className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
                  >
                    Cancel Event
                  </button>
                )}
              </div>
              {event.is_cancelled && (
                <p className="text-red-600 mt-4">This event has been cancelled</p>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ManageEvents;
