import React, { useState, useEffect } from 'react';
import { getEvents, cancelEvent } from '../services/api';
import { Event } from '../types';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const ManageEvents: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const { user } = useAuth();

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const eventsData = await getEvents();
      const userEvents = eventsData.filter(event => event.organizer_id === user?.id);
      setEvents(userEvents);
    } catch (err) {
      setError('Failed to load events');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelEvent = async (eventId: number) => {
    try {
      await cancelEvent(eventId);
      setEvents(prevEvents => prevEvents.filter(event => event.id !== eventId));
      setSuccess('Event cancelled successfully');
    } catch (err) {
      setError('Failed to cancel event');
    }
  };

  const formatDateTime = (date: string, time: string) => {
    console.log('Raw date:', date, 'Raw time:', time);

    // Parse the ISO date string
    const dateObj = new Date(date);
    const timeObj = new Date(time);

    // Combine the date from dateObj with the time from timeObj
    const combinedDateTime = new Date(
      dateObj.getFullYear(),
      dateObj.getMonth(),
      dateObj.getDate(),
      timeObj.getHours(),
      timeObj.getMinutes()
    );

    console.log('Combined DateTime:', combinedDateTime);

    // Format the date and time
    const formatted = combinedDateTime.toLocaleString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true
    });

    console.log('Formatted result:', formatted);
    return formatted;
  };

  if (loading) {
    return <div className="flex justify-center items-center min-h-screen">Loading...</div>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-8">Events Manager</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}
      {success && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
          {success}
        </div>
      )}

      {events.length === 0 ? (
        <div className="text-center py-12">
          <div className="inline-flex items-center justify-center w-24 h-24 rounded-full bg-gray-100 mb-4">
            <svg
              className="w-12 h-12 text-gray-400"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10"
              />
            </svg>
          </div>
          <h3 className="text-lg font-medium text-gray-900">
            No events created yet
          </h3>
          <p className="mt-2 text-sm text-gray-500">
            Create your first event to get started!
          </p>
          <div className="mt-6">
            <Link
              to="/create-event"
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-[#C4E538] hover:bg-[#B4D532] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538]"
            >
              Create Your First Event
            </Link>
          </div>
        </div>
      ) : (
        <div className="space-y-8">
          {/* Active Events Section */}
          <div>
            <h2 className="text-xl font-semibold text-gray-900 mb-4">Active Events</h2>
            <div className="space-y-4">
              {events.filter(event => !event.is_cancelled).map(event => (
                <div key={event.id} className="bg-white shadow rounded-lg p-6">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="text-xl font-semibold">{event.court_location}</h3>
                      <p className="text-gray-600 mt-2">
                        {formatDateTime(event.event_date, event.event_time)}
                      </p>
                      <p className="text-sm text-gray-500 mt-1">
                        {event.registrations?.length || 0} participants
                        {event.max_participants && ` / ${event.max_participants} max`}
                      </p>
                    </div>
                    <button
                      onClick={() => handleCancelEvent(event.id)}
                      className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
                    >
                      Cancel Event
                    </button>
                  </div>
                </div>
              ))}
              {events.filter(event => !event.is_cancelled).length === 0 && (
                <p className="text-gray-500 text-center py-4">No active events</p>
              )}
            </div>
          </div>

          {/* Cancelled Events Section */}
          <div>
            <h2 className="text-xl font-semibold text-gray-900 mb-4">Cancelled Events</h2>
            <div className="space-y-4">
              {events.filter(event => event.is_cancelled).map(event => (
                <div key={event.id} className="bg-white shadow rounded-lg p-6 opacity-75">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="text-xl font-semibold">{event.court_location}</h3>
                      <p className="text-gray-600 mt-2">
                        {formatDateTime(event.event_date, event.event_time)}
                      </p>
                      <p className="text-sm text-gray-500 mt-1">
                        {event.registrations?.length || 0} participants
                        {event.max_participants && ` / ${event.max_participants} max`}
                      </p>
                      <p className="text-red-600 mt-2">This event has been cancelled</p>
                    </div>
                  </div>
                </div>
              ))}
              {events.filter(event => event.is_cancelled).length === 0 && (
                <p className="text-gray-500 text-center py-4">No cancelled events</p>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageEvents;
