import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getEvents, registerForEvent } from '../services/api';
import { Event } from '../types';
import { format } from 'date-fns';

const Dashboard: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const data = await getEvents();
      setEvents(data);
      setError(null);
    } catch (err) {
      setError('Failed to load events');
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (eventId: number) => {
    try {
      await registerForEvent(eventId);
      // Refresh events after registration
      fetchEvents();
    } catch (err) {
      setError('Failed to register for event');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg text-gray-600">Loading events...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Available Events</h1>
        <Link
          to="/create-event"
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
        >
          Create Event
        </Link>
      </div>

      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {events.map((event) => (
          <div
            key={event.id}
            className="bg-white overflow-hidden shadow rounded-lg"
          >
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg font-medium text-gray-900">
                Tennis Match at {event.court_location}
              </h3>
              <div className="mt-2 text-sm text-gray-500">
                <p>
                  Date:{' '}
                  {format(new Date(event.event_date), 'MMMM d, yyyy')}
                </p>
                <p>
                  Time:{' '}
                  {format(new Date(event.event_time), 'h:mm a')}
                </p>
                {event.max_participants && (
                  <p>
                    Spots:{' '}
                    {event.max_participants - (event.registrations?.length || 0)}/
                    {event.max_participants}
                  </p>
                )}
              </div>
              <div className="mt-4">
                <button
                  onClick={() => handleRegister(event.id)}
                  disabled={event.is_cancelled}
                  className={`w-full inline-flex justify-center items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white ${
                    event.is_cancelled
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500'
                  }`}
                >
                  {event.is_cancelled
                    ? 'Event Cancelled'
                    : 'Register for Event'}
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {events.length === 0 && (
        <div className="text-center py-12">
          <h3 className="text-lg font-medium text-gray-900">
            No events available
          </h3>
          <p className="mt-2 text-sm text-gray-500">
            Be the first to create an event!
          </p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
