import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { getEvents, registerForEvent, cancelEvent } from '../services/api';
import { Event } from '../types';
import { format, isThisWeek, isThisMonth, addWeeks, startOfWeek, endOfWeek, isPast, parseISO } from 'date-fns';
import { useAuth } from '../contexts/AuthContext';
import LocationMap from '../components/LocationMap';
import EventCard from '../components/EventCard';
import { debounce } from 'lodash';

const Dashboard: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();
  const [toast, setToast] = useState<{ message: string; type: 'success' | 'error' } | null>(null);
  const [selectedEvent, setSelectedEvent] = useState<Event | null>(null);
  const [expandedSections, setExpandedSections] = useState({
    upcoming: true,
    past: false
  });
  const [searchCity, setSearchCity] = useState('');
  const [mileRange, setMileRange] = useState(20);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [selectedLocation, setSelectedLocation] = useState<{ lat: number; lon: number; display_name: string } | null>(null);
  const navigate = useNavigate();

  const MAJOR_US_CITIES = [
    // New York City and Boroughs
    { name: 'New York', lat: 40.7128, lon: -74.0060 },
    { name: 'Brooklyn', lat: 40.6782, lon: -73.9442 },
    { name: 'Queens', lat: 40.7282, lon: -73.7949 },
    { name: 'Bronx', lat: 40.8448, lon: -73.8648 },
    { name: 'Manhattan', lat: 40.7831, lon: -73.9712 },
    { name: 'Staten Island', lat: 40.5795, lon: -74.1502 },
    // Los Angeles Area
    { name: 'Los Angeles', lat: 34.0522, lon: -118.2437 },
    { name: 'Hollywood', lat: 34.0928, lon: -118.3287 },
    { name: 'Santa Monica', lat: 34.0195, lon: -118.4912 },
    { name: 'Beverly Hills', lat: 34.0736, lon: -118.4004 },
    // Chicago Area
    { name: 'Chicago', lat: 41.8781, lon: -87.6298 },
    { name: 'Evanston', lat: 42.0451, lon: -87.6877 },
    { name: 'Oak Park', lat: 41.8850, lon: -87.7845 },
    // Houston Area
    { name: 'Houston', lat: 29.7604, lon: -95.3698 },
    { name: 'Sugar Land', lat: 29.6197, lon: -95.6349 },
    { name: 'The Woodlands', lat: 30.1658, lon: -95.4612 },
    // Phoenix Area
    { name: 'Phoenix', lat: 33.4484, lon: -112.0740 },
    { name: 'Scottsdale', lat: 33.4942, lon: -111.9261 },
    { name: 'Tempe', lat: 33.4255, lon: -111.9400 },
    // Philadelphia Area
    { name: 'Philadelphia', lat: 39.9526, lon: -75.1652 },
    { name: 'Center City', lat: 39.9526, lon: -75.1652 },
    { name: 'University City', lat: 39.9522, lon: -75.1932 },
    // San Antonio Area
    { name: 'San Antonio', lat: 29.4241, lon: -98.4936 },
    { name: 'Alamo Heights', lat: 29.4847, lon: -98.4658 },
    // San Diego Area
    { name: 'San Diego', lat: 32.7157, lon: -117.1611 },
    { name: 'La Jolla', lat: 32.8328, lon: -117.2713 },
    { name: 'Coronado', lat: 32.6859, lon: -117.1831 },
    // Dallas Area
    { name: 'Dallas', lat: 32.7767, lon: -96.7970 },
    { name: 'Plano', lat: 33.0198, lon: -96.6989 },
    { name: 'Irving', lat: 32.8140, lon: -96.9489 },
    // San Jose Area
    { name: 'San Jose', lat: 37.3382, lon: -121.8863 },
    { name: 'Palo Alto', lat: 37.4419, lon: -122.1430 },
    { name: 'Mountain View', lat: 37.3861, lon: -122.0839 },
    // Austin Area
    { name: 'Austin', lat: 30.2672, lon: -97.7431 },
    { name: 'Round Rock', lat: 30.5083, lon: -97.6789 },
    // Jacksonville Area
    { name: 'Jacksonville', lat: 30.3322, lon: -81.6557 },
    { name: 'Jacksonville Beach', lat: 30.2947, lon: -81.3931 },
    // Fort Worth Area
    { name: 'Fort Worth', lat: 32.7555, lon: -97.3308 },
    { name: 'Arlington', lat: 32.7357, lon: -97.1081 },
    // Columbus Area
    { name: 'Columbus', lat: 39.9612, lon: -82.9988 },
    { name: 'Dublin', lat: 40.0992, lon: -83.1141 },
    // Charlotte Area
    { name: 'Charlotte', lat: 35.2271, lon: -80.8431 },
    { name: 'Ballantyne', lat: 35.0527, lon: -80.8456 },
    // San Francisco Area
    { name: 'San Francisco', lat: 37.7749, lon: -122.4194 },
    { name: 'Sausalito', lat: 37.8591, lon: -122.4853 },
    { name: 'Tiburon', lat: 37.8735, lon: -122.4566 },
    // Indianapolis Area
    { name: 'Indianapolis', lat: 39.7684, lon: -86.1581 },
    { name: 'Carmel', lat: 39.9784, lon: -86.1180 },
    // Seattle Area
    { name: 'Seattle', lat: 47.6062, lon: -122.3321 },
    { name: 'Bellevue', lat: 47.6101, lon: -122.2015 },
    { name: 'Kirkland', lat: 47.6769, lon: -122.2060 },
    // Denver Area
    { name: 'Denver', lat: 39.7392, lon: -104.9903 },
    { name: 'Boulder', lat: 40.0150, lon: -105.2705 },
    { name: 'Cherry Creek', lat: 39.7167, lon: -104.9500 },
    // Washington DC Area
    { name: 'Washington DC', lat: 38.9072, lon: -77.0369 },
    { name: 'Georgetown', lat: 38.9097, lon: -77.0654 },
    { name: 'Arlington VA', lat: 38.8816, lon: -77.0910 },
    // Boston Area
    { name: 'Boston', lat: 42.3601, lon: -71.0589 },
    { name: 'Cambridge', lat: 42.3736, lon: -71.1097 },
    { name: 'Brookline', lat: 42.3318, lon: -71.1212 },
    // Rest of major cities
    { name: 'El Paso', lat: 31.7619, lon: -106.4850 },
    { name: 'Nashville', lat: 36.1627, lon: -86.7816 },
    { name: 'Detroit', lat: 42.3314, lon: -83.0458 },
    { name: 'Portland', lat: 45.5155, lon: -122.6789 },
    { name: 'Las Vegas', lat: 36.1699, lon: -115.1398 },
    { name: 'Oklahoma City', lat: 35.4676, lon: -97.5164 },
    { name: 'Memphis', lat: 35.1495, lon: -90.0490 },
    { name: 'Louisville', lat: 38.2527, lon: -85.7585 },
    { name: 'Baltimore', lat: 39.2904, lon: -76.6122 },
    { name: 'Milwaukee', lat: 43.0389, lon: -87.9065 },
    { name: 'Albuquerque', lat: 35.0844, lon: -106.6504 },
    { name: 'Tucson', lat: 32.2226, lon: -110.9747 },
    { name: 'Fresno', lat: 36.7378, lon: -119.7871 },
    { name: 'Sacramento', lat: 38.5816, lon: -121.4944 },
    { name: 'Long Beach', lat: 33.7701, lon: -118.1937 },
    { name: 'Kansas City', lat: 39.0997, lon: -94.5786 },
    { name: 'Mesa', lat: 33.4152, lon: -111.8315 },
    { name: 'Atlanta', lat: 33.7490, lon: -84.3880 },
    { name: 'Colorado Springs', lat: 38.8339, lon: -104.8214 },
    { name: 'Miami', lat: 25.7617, lon: -80.1918 },
    { name: 'Raleigh', lat: 35.7796, lon: -78.6382 },
    { name: 'Omaha', lat: 41.2565, lon: -95.9345 },
    { name: 'Minneapolis', lat: 44.9778, lon: -93.2650 },
    { name: 'Tulsa', lat: 36.1540, lon: -95.9928 },
    { name: 'Cleveland', lat: 41.4993, lon: -81.6944 },
    { name: 'Wichita', lat: 37.6872, lon: -97.3301 },
    { name: 'New Orleans', lat: 29.9511, lon: -90.0715 },
    { name: 'Bakersfield', lat: 35.3733, lon: -119.0187 },
    { name: 'Tampa', lat: 27.9506, lon: -82.4572 }
  ];

  useEffect(() => {
    fetchEvents();
  }, []);

  // Auto-hide toast after 3 seconds
  useEffect(() => {
    if (toast) {
      const timer = setTimeout(() => {
        setToast(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [toast]);

  const fetchEvents = async () => {
    try {
      const data = await getEvents();
      // Sort events by date
      const sortedEvents = data.sort((a, b) => 
        new Date(a.event_date).getTime() - new Date(b.event_date).getTime()
      );
      setEvents(sortedEvents);
      setError(null);
    } catch (err: any) {
      console.error('Failed to load events:', err);
      let errorMsg = 'Failed to load events';
      if (err.detail) {
        errorMsg = typeof err.detail === 'string' ? err.detail : JSON.stringify(err.detail);
      } else if (err.message) {
        errorMsg = err.message;
      }
      setError(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  const isRegisteredForEvent = (eventId: number) => {
    return events.find(event => event.id === eventId)?.registrations?.some(r => r.user_id === user?.id) || false;
  };

  const isEventCreator = (eventId: number) => {
    return events.find(event => event.id === eventId)?.organizer_id === user?.id;
  };

  const handleCancelEvent = async (eventId: number) => {
    try {
      await cancelEvent(eventId);
      fetchEvents();
    } catch (error) {
      console.error('Error cancelling event:', error);
    }
  };

  const handleRegister = async (eventId: number) => {
    try {
      await registerForEvent(eventId);
      fetchEvents();
    } catch (error) {
      console.error('Error registering for event:', error);
    }
  };

  const handleWithdraw = async (eventId: number) => {
    try {
      await registerForEvent(eventId, true);
      fetchEvents();
    } catch (error) {
      console.error('Error withdrawing from event:', error);
    }
  };

  const handleLocationSelect = (location: any) => {
    setSelectedLocation(location);
    setSearchQuery(location.display_name);
    setShowResults(false);
  };

  const searchAddress = debounce(async (query: string) => {
    if (!query.trim()) {
      setSearchResults([]);
      setShowResults(false);
      return;
    }

    setIsSearching(true);
    try {
      const url = `https://cors-anywhere.herokuapp.com/https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
        query
      )}&countrycodes=us&limit=5`;

      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Accept-Language': 'en-US,en;q=0.9',
          'User-Agent': 'RacketBuddy/1.0',
          'Accept': 'application/json',
          'Origin': window.location.origin,
          'X-Requested-With': 'XMLHttpRequest'
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (data && Array.isArray(data)) {
        setSearchResults(data);
        setShowResults(true);
      } else {
        setSearchResults([]);
        setShowResults(false);
      }
    } catch (error) {
      console.error('Error searching address:', error);
      setSearchResults([]);
      setShowResults(false);
    } finally {
      setIsSearching(false);
    }
  }, 300);

  useEffect(() => {
    searchAddress(searchQuery);
  }, [searchQuery]);

  const toggleSection = (section: keyof typeof expandedSections) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const calculateDistance = (lat1: number, lon1: number, lat2: number, lon2: number) => {
    const R = 3959; // Earth's radius in miles
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const filterEventsByDistance = (events: Event[]) => {
    if (!selectedLocation) return events;

    return events.filter(event => {
      if (!event.latitude || !event.longitude) return false;

      const distance = calculateDistance(
        selectedLocation.lat,
        selectedLocation.lon,
        event.latitude,
        event.longitude
      );

      return distance <= mileRange;
    });
  };

  const categorizeEvents = () => {
    const now = new Date();
    const filteredEvents = filterEventsByDistance(events);
    return {
      upcoming: filteredEvents.filter(event => new Date(event.event_date) >= now && !event.is_cancelled),
      past: filteredEvents.filter(event => new Date(event.event_date) < now && !event.is_cancelled)
    };
  };

  const { upcoming, past } = categorizeEvents();
  const hasAnyEvents = upcoming.length > 0 || past.length > 0;

  const renderEventCard = (event: Event) => {
    return (
      <EventCard
        key={event.id}
        event={event}
        onRegister={() => handleRegister(event.id)}
        onWithdraw={() => handleWithdraw(event.id)}
        onCancel={() => handleCancelEvent(event.id)}
        isRegistered={event.registrations?.some(r => r.user_id === user?.id)}
        isCreator={event.organizer_id === user?.id}
      />
    );
  };

  const renderEventSection = (title: string, events: Event[], sectionKey: keyof typeof expandedSections) => (
    <div className="mb-8">
      <button
        onClick={() => toggleSection(sectionKey)}
        className="w-full flex justify-between items-center text-2xl font-bold text-gray-900 mb-4 hover:text-[#C4E538] transition-colors"
      >
        <span>{title}</span>
        <span className="text-lg">
          {expandedSections[sectionKey] ? '▼' : '▶'}
        </span>
      </button>
      {expandedSections[sectionKey] && events.length > 0 ? (
        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {events.map(renderEventCard)}
        </div>
      ) : (
        <p className="text-gray-500 text-center py-4">
          {expandedSections[sectionKey] ? 'No events in this period' : ''}
        </p>
      )}
    </div>
  );

  const handleCityChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setSearchCity(value);

    if (value) {
      const filtered = MAJOR_US_CITIES.filter(city => 
        city.name.toLowerCase().includes(value.toLowerCase())
      );
      setSearchResults(filtered);
    } else {
      setSearchResults([]);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-extrabold text-gray-900 sm:text-5xl sm:tracking-tight lg:text-6xl">
            Find Events
          </h1>
          <p className="mt-5 max-w-xl mx-auto text-xl text-gray-500">
            {selectedLocation ? `Events within ${mileRange} miles of ${selectedLocation.display_name}` : 'Select a location to search for events'}
          </p>
        </div>

        {/* Location Search */}
        <div className="max-w-3xl mx-auto mb-12 px-4">
          <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
            <div className="relative flex-1 max-w-xl">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Enter city, state, or zip code"
                className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              />
              {isSearching && (
                <div className="absolute right-3 top-3">
                  <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-500"></div>
                </div>
              )}
              {showResults && searchResults.length > 0 && (
                <div className="absolute z-10 w-full mt-1 bg-white rounded-md shadow-lg">
                  <ul className="max-h-60 rounded-md py-1 text-base overflow-auto focus:outline-none sm:text-sm">
                    {searchResults.map((result) => (
                      <li
                        key={result.place_id}
                        className="cursor-pointer select-none relative py-2 pl-3 pr-9 hover:bg-blue-50"
                        onClick={() => handleLocationSelect(result)}
                      >
                        <div className="flex items-center">
                          <span className="ml-3 truncate">{result.display_name}</span>
                        </div>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
            <div className="flex items-center gap-2 min-w-[200px]">
              <input
                type="range"
                min="1"
                max="100"
                value={mileRange}
                onChange={(e) => setMileRange(Number(e.target.value))}
                className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer [&::-webkit-slider-thumb]:appearance-none [&::-webkit-slider-thumb]:w-4 [&::-webkit-slider-thumb]:h-4 [&::-webkit-slider-thumb]:rounded-full [&::-webkit-slider-thumb]:bg-[#C4E538] [&::-webkit-slider-thumb]:cursor-pointer [&::-moz-range-thumb]:w-4 [&::-moz-range-thumb]:h-4 [&::-moz-range-thumb]:rounded-full [&::-moz-range-thumb]:bg-[#C4E538] [&::-moz-range-thumb]:border-0 [&::-moz-range-thumb]:cursor-pointer [&::-moz-range-track]:bg-gray-200 [&::-moz-range-track]:rounded-lg [&::-moz-range-track]:h-2"
                style={{
                  background: `linear-gradient(to right, #C4E538 0%, #C4E538 ${(mileRange - 1) * (100 / 99)}%, #E5E7EB ${(mileRange - 1) * (100 / 99)}%, #E5E7EB 100%)`
                }}
              />
              <span className="text-sm font-medium text-[#C4E538] whitespace-nowrap">{mileRange} miles</span>
            </div>
          </div>
        </div>

        {/* Events List */}
        {selectedLocation ? (
          <>
            {hasAnyEvents ? (
              <div className="space-y-12">
                {upcoming.length > 0 && (
                  <div>
                    <h2 className="text-2xl font-bold text-gray-900 mb-6">Upcoming Events</h2>
                    <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
                      {upcoming.map((event) => (
                        <EventCard
                          key={event.id}
                          event={event}
                          onRegister={() => handleRegister(event.id)}
                          onWithdraw={() => handleWithdraw(event.id)}
                          onCancel={() => handleCancelEvent(event.id)}
                          isRegistered={event.registrations?.some(r => r.user_id === user?.id)}
                          isCreator={event.organizer_id === user?.id}
                        />
                      ))}
                    </div>
                  </div>
                )}

                {past.length > 0 && (
                  <div>
                    <h2 className="text-2xl font-bold text-gray-900 mb-6">Past Events</h2>
                    <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
                      {past.map((event) => (
                        <EventCard
                          key={event.id}
                          event={event}
                          onRegister={() => handleRegister(event.id)}
                          onWithdraw={() => handleWithdraw(event.id)}
                          onCancel={() => handleCancelEvent(event.id)}
                          isRegistered={event.registrations?.some(r => r.user_id === user?.id)}
                          isCreator={event.organizer_id === user?.id}
                        />
                      ))}
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <div className="text-center py-12">
                <p className="text-xl text-gray-500">No events found in this area</p>
              </div>
            )}
          </>
        ) : (
          <div className="text-center py-12">
            <p className="text-xl text-gray-500">Please select a location to view events</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
