import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import { debounce } from 'lodash';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Grid,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  Box,
  CircularProgress,
  Autocomplete
} from '@mui/material';
import { Event } from '../types/event';

interface LocationSuggestion {
  display_name: string;
  lat: string;
  lon: string;
}

const CreateEvent: React.FC = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const { showToast } = useToast();
  const [loading, setLoading] = useState(false);
  const [searchLoading, setSearchLoading] = useState(false);
  const [locationSuggestions, setLocationSuggestions] = useState<LocationSuggestion[]>([]);
  const [selectedLocation, setSelectedLocation] = useState<LocationSuggestion | null>(null);
  const searchTimeoutRef = useRef<NodeJS.Timeout>();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    event_date: '',
    event_time: '',
    location: '',
    latitude: '',
    longitude: '',
    maxParticipants: 2,
    skillLevel: 'beginner',
    eventType: 'singles',
    price: 0
  });

  const [errors, setErrors] = useState({
    title: '',
    description: '',
    event_date: '',
    event_time: '',
    location: '',
    maxParticipants: '',
    price: ''
  });

  const validateForm = () => {
    const newErrors = {
      title: '',
      description: '',
      event_date: '',
      event_time: '',
      location: '',
      maxParticipants: '',
      price: ''
    };

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }
    if (!formData.description.trim()) {
      newErrors.description = 'Description is required';
    }
    if (!formData.event_date) {
      newErrors.event_date = 'Date is required';
    }
    if (!formData.event_time) {
      newErrors.event_time = 'Time is required';
    }
    if (!formData.location.trim()) {
      newErrors.location = 'Location is required';
    }
    if (formData.maxParticipants < 2) {
      newErrors.maxParticipants = 'Minimum 2 participants required';
    }
    if (formData.price < 0) {
      newErrors.price = 'Price cannot be negative';
    }

    setErrors(newErrors);
    return !Object.values(newErrors).some(error => error);
  };

  const handleLocationSearch = async (query: string) => {
    if (!query.trim()) {
      setLocationSuggestions([]);
      return;
    }

    try {
      setSearchLoading(true);
      const response = await fetch(`http://localhost:8000/api/geocode?query=${encodeURIComponent(query)}`);
      if (!response.ok) throw new Error('Failed to fetch locations');
      const data = await response.json();
      setLocationSuggestions(data);
    } catch (error) {
      console.error('Error fetching locations:', error);
      showToast('Error fetching location suggestions', 'error');
    } finally {
      setSearchLoading(false);
    }
  };

  const debouncedLocationSearch = debounce(handleLocationSearch, 300);

  const handleLocationChange = (event: React.SyntheticEvent, value: string) => {
    setFormData(prev => ({ ...prev, location: value }));
    debouncedLocationSearch(value);
  };

  const handleLocationSelect = (event: React.SyntheticEvent, value: LocationSuggestion | null) => {
    setSelectedLocation(value);
    if (value) {
      setFormData(prev => ({
        ...prev,
        location: value.display_name,
        latitude: value.lat,
        longitude: value.lon
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      setLoading(true);
      const eventDateTime = new Date(`${formData.event_date}T${formData.event_time}`);
      
      const response = await fetch('http://localhost:8000/api/events', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          ...formData,
          date: eventDateTime.toISOString()
        })
      });

      if (!response.ok) {
        throw new Error('Failed to create event');
      }

      showToast('Event created successfully!', 'success');
      navigate('/events');
    } catch (error) {
      console.error('Error creating event:', error);
      showToast('Failed to create event', 'error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Create New Event
        </Typography>

        <form onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Event Title"
                value={formData.title}
                onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                error={!!errors.title}
                helperText={errors.title}
                required
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                fullWidth
                multiline
                rows={4}
                label="Description"
                value={formData.description}
                onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                error={!!errors.description}
                helperText={errors.description}
                required
              />
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                type="date"
                label="Date"
                value={formData.event_date}
                onChange={(e) => setFormData(prev => ({ ...prev, event_date: e.target.value }))}
                error={!!errors.event_date}
                helperText={errors.event_date}
                required
                InputLabelProps={{ shrink: true }}
              />
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                type="time"
                label="Time"
                value={formData.event_time}
                onChange={(e) => setFormData(prev => ({ ...prev, event_time: e.target.value }))}
                error={!!errors.event_time}
                helperText={errors.event_time}
                required
                InputLabelProps={{ shrink: true }}
              />
            </Grid>

            <Grid item xs={12}>
              <Autocomplete
                freeSolo
                options={locationSuggestions}
                getOptionLabel={(option) => 
                  typeof option === 'string' ? option : option.display_name
                }
                value={formData.location}
                onChange={handleLocationSelect}
                onInputChange={handleLocationChange}
                loading={searchLoading}
                renderInput={(params) => (
                  <TextField
                    {...params}
                    label="Location"
                    error={!!errors.location}
                    helperText={errors.location}
                    required
                    InputProps={{
                      ...params.InputProps,
                      endAdornment: (
                        <>
                          {searchLoading ? <CircularProgress color="inherit" size={20} /> : null}
                          {params.InputProps.endAdornment}
                        </>
                      ),
                    }}
                  />
                )}
                renderOption={(props, option) => (
                  <li {...props}>
                    <Box>
                      <Typography variant="body1">{option.display_name}</Typography>
                    </Box>
                  </li>
                )}
              />
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                type="number"
                label="Maximum Participants"
                value={formData.maxParticipants}
                onChange={(e) => setFormData(prev => ({ ...prev, maxParticipants: parseInt(e.target.value) }))}
                error={!!errors.maxParticipants}
                helperText={errors.maxParticipants}
                inputProps={{ min: 2 }}
                required
              />
            </Grid>

            <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>Skill Level</InputLabel>
                <Select
                  value={formData.skillLevel}
                  onChange={(e) => setFormData(prev => ({ ...prev, skillLevel: e.target.value }))}
                  label="Skill Level"
                >
                  <MenuItem value="beginner">Beginner</MenuItem>
                  <MenuItem value="intermediate">Intermediate</MenuItem>
                  <MenuItem value="advanced">Advanced</MenuItem>
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>Event Type</InputLabel>
                <Select
                  value={formData.eventType}
                  onChange={(e) => setFormData(prev => ({ ...prev, eventType: e.target.value }))}
                  label="Event Type"
                >
                  <MenuItem value="singles">Singles</MenuItem>
                  <MenuItem value="doubles">Doubles</MenuItem>
                  <MenuItem value="mixed">Mixed</MenuItem>
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                type="number"
                label="Price ($)"
                value={formData.price}
                onChange={(e) => setFormData(prev => ({ ...prev, price: parseFloat(e.target.value) }))}
                error={!!errors.price}
                helperText={errors.price}
                inputProps={{ min: 0, step: 0.01 }}
              />
            </Grid>

            <Grid item xs={12}>
              <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                <Button
                  variant="outlined"
                  onClick={() => navigate('/events')}
                  disabled={loading}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  variant="contained"
                  disabled={loading}
                >
                  {loading ? <CircularProgress size={24} /> : 'Create Event'}
                </Button>
              </Box>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container>
  );
};

export default CreateEvent;
