import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icons in Leaflet
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

interface LocationMapProps {
  location: string;
  latitude?: string;
  longitude?: string;
}

const LocationMap: React.FC<LocationMapProps> = ({ location, latitude, longitude }) => {
  // Default position (USA)
  const defaultPosition: [number, number] = [37.0902, -95.7129];
  
  // Use provided coordinates if available, otherwise use default
  const position: [number, number] = latitude && longitude 
    ? [parseFloat(latitude), parseFloat(longitude)]
    : defaultPosition;

  return (
    <div className="h-48 w-full rounded-lg overflow-hidden">
      <MapContainer
        center={position}
        zoom={latitude && longitude ? 15 : 4}
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        <Marker position={position}>
          <Popup>
            {location}
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default LocationMap;
