import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { createEvent } from '../services/api';
import { debounce } from 'lodash';

interface SearchResult {
  display_name: string;
  lat: string;
  lon: string;
}

declare global {
  interface Window {
    google: any;
  }
}

const CreateEvent: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    address: '',
    latitude: 0,
    longitude: 0,
    event_date: '',
    event_time: '',
    max_participants: '',
    description: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [autocomplete, setAutocomplete] = useState<any>(null);
  const searchInputRef = React.useRef<HTMLInputElement>(null);

  useEffect(() => {
    // Load Google Maps script
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&libraries=places`;
    script.async = true;
    script.defer = true;
    script.onload = initializeAutocomplete;
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  const initializeAutocomplete = () => {
    if (searchInputRef.current && window.google) {
      const autocomplete = new window.google.maps.places.Autocomplete(searchInputRef.current, {
        types: ['address', 'establishment', 'geocode'],
        componentRestrictions: { country: 'us' }
      });

      autocomplete.addListener('place_changed', () => {
        const place = autocomplete.getPlace();
        if (place.geometry) {
          setFormData(prev => ({
            ...prev,
            address: place.formatted_address,
            latitude: place.geometry.location.lat(),
            longitude: place.geometry.location.lng()
          }));
        }
      });

      setAutocomplete(autocomplete);
    }
  };

  const handleAddressChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setFormData(prev => ({ ...prev, address: value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const eventDateTime = new Date(`${formData.event_date}T${formData.event_time}`);
      
      const eventData = {
        ...formData,
        max_participants: formData.max_participants ? parseInt(formData.max_participants) : undefined,
        event_date: eventDateTime.toISOString(),
        event_time: eventDateTime.toISOString(),
      };
      await createEvent(eventData);
      navigate('/events');
    } catch (err: any) {
      console.error('Create event error:', err);
      let errorMsg = 'Failed to create event';
      if (err.detail) {
        errorMsg = typeof err.detail === 'string' ? err.detail : JSON.stringify(err.detail);
      } else if (err.message) {
        errorMsg = err.message;
      }
      setError(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-3xl mx-auto">
        <div className="bg-white shadow sm:rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900 mb-6">
              Create New Event
            </h3>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="address-search-container">
                <label
                  htmlFor="address"
                  className="block text-sm font-medium text-gray-700"
                >
                  Location
                </label>
                <div className="mt-1 relative">
                  <input
                    ref={searchInputRef}
                    type="text"
                    name="address"
                    id="address"
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                    value={formData.address}
                    onChange={handleAddressChange}
                    placeholder="Search for a location"
                  />
                </div>
              </div>

              <div>
                <label
                  htmlFor="event_date"
                  className="block text-sm font-medium text-gray-700"
                >
                  Date
                </label>
                <input
                  type="date"
                  name="event_date"
                  id="event_date"
                  required
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                  value={formData.event_date}
                  onChange={handleChange}
                />
              </div>

              <div>
                <label
                  htmlFor="event_time"
                  className="block text-sm font-medium text-gray-700"
                >
                  Time
                </label>
                <input
                  type="time"
                  name="event_time"
                  id="event_time"
                  required
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                  value={formData.event_time}
                  onChange={handleChange}
                />
              </div>

              <div>
                <label
                  htmlFor="max_participants"
                  className="block text-sm font-medium text-gray-700"
                >
                  Max Participants
                </label>
                <input
                  type="number"
                  name="max_participants"
                  id="max_participants"
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                  value={formData.max_participants}
                  onChange={handleChange}
                  placeholder="Enter max participants"
                />
              </div>

              <div>
                <label
                  htmlFor="description"
                  className="block text-sm font-medium text-gray-700"
                >
                  Description
                </label>
                <textarea
                  name="description"
                  id="description"
                  rows={3}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                  value={formData.description}
                  onChange={handleChange}
                  placeholder="Enter event description"
                />
              </div>

              {error && (
                <div className="text-red-600 text-sm">{error}</div>
              )}

              <div>
                <button
                  type="submit"
                  disabled={loading}
                  className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-gray-900 bg-[#C4E538] hover:bg-[#B4D532] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538] disabled:opacity-50"
                >
                  {loading ? 'Creating...' : 'Create Event'}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateEvent;
