import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { createEvent } from '../services/api';
import { debounce } from 'lodash';

interface SearchResult {
  display_name: string;
  lat: string;
  lon: string;
}

const CreateEvent: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    court_location: '',
    address: '',
    event_date: '',
    event_time: '',
    max_participants: '',
    description: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [showResults, setShowResults] = useState(false);

  // Cleanup function when component unmounts
  useEffect(() => {
    return () => {
      setSearchResults([]);
      setShowResults(false);
    };
  }, []);

  const searchAddress = debounce(async (query: string) => {
    if (!query.trim()) {
      setSearchResults([]);
      setShowResults(false);
      return;
    }

    setIsSearching(true);
    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
          query
        )}&countrycodes=us&limit=5`,
        {
          headers: {
            'Accept-Language': 'en-US,en;q=0.9',
          },
        }
      );
      const data = await response.json();
      setSearchResults(data);
      setShowResults(true);
    } catch (error) {
      console.error('Error searching address:', error);
    } finally {
      setIsSearching(false);
    }
  }, 500);

  const handleAddressChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setFormData(prev => ({ ...prev, address: value }));
    searchAddress(value);
  };

  const handleAddressSelect = (result: SearchResult) => {
    setFormData(prev => ({
      ...prev,
      address: result.display_name,
      court_location: result.display_name.split(',')[0] // Use the first part of the address as court name
    }));
    setSearchResults([]);
    setShowResults(false);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSearchResults([]);
    setShowResults(false);

    try {
      const eventDateTime = new Date(`${formData.event_date}T${formData.event_time}`);
      
      const eventData = {
        ...formData,
        max_participants: formData.max_participants ? parseInt(formData.max_participants) : undefined,
        event_date: eventDateTime.toISOString(),
        event_time: eventDateTime.toISOString(),
      };
      await createEvent(eventData);
      navigate('/events');
    } catch (err: any) {
      console.error('Create event error:', err);
      let errorMsg = 'Failed to create event';
      if (err.detail) {
        errorMsg = typeof err.detail === 'string' ? err.detail : JSON.stringify(err.detail);
      } else if (err.message) {
        errorMsg = err.message;
      }
      setError(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle click outside to close dropdown
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const target = event.target as HTMLElement;
      if (!target.closest('.address-search-container')) {
        setShowResults(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-3xl mx-auto">
        <div className="bg-white shadow sm:rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900 mb-6">
              Create New Event
            </h3>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="address-search-container">
                <label
                  htmlFor="address"
                  className="block text-sm font-medium text-gray-700"
                >
                  Location
                </label>
                <div className="mt-1 relative">
                  <input
                    type="text"
                    name="address"
                    id="address"
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                    value={formData.address}
                    onChange={handleAddressChange}
                    placeholder="Search for a location"
                  />
                  {isSearching && (
                    <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-[#C4E538]"></div>
                    </div>
                  )}
                  {showResults && searchResults.length > 0 && (
                    <div className="absolute z-10 mt-1 w-full bg-white shadow-lg max-h-60 rounded-md py-1 text-base overflow-auto focus:outline-none sm:text-sm">
                      {searchResults.map((result, index) => (
                        <div
                          key={index}
                          className="cursor-pointer select-none relative py-2 pl-3 pr-9 hover:bg-gray-100"
                          onClick={() => handleAddressSelect(result)}
                        >
                          <div className="flex items-center">
                            <span className="ml-3 truncate">{result.display_name}</span>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>

              <div>
                <label
                  htmlFor="court_location"
                  className="block text-sm font-medium text-gray-700"
                >
                  Court Name
                </label>
                <input
                  type="text"
                  name="court_location"
                  id="court_location"
                  required
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                  value={formData.court_location}
                  onChange={handleChange}
                  placeholder="Enter court name"
                />
              </div>

              <div>
                <label
                  htmlFor="event_date"
                  className="block text-sm font-medium text-gray-700"
                >
                  Date
                </label>
                <input
                  type="date"
                  name="event_date"
                  id="event_date"
                  required
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                  value={formData.event_date}
                  onChange={handleChange}
                />
              </div>

              <div>
                <label
                  htmlFor="event_time"
                  className="block text-sm font-medium text-gray-700"
                >
                  Time
                </label>
                <input
                  type="time"
                  name="event_time"
                  id="event_time"
                  required
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                  value={formData.event_time}
                  onChange={handleChange}
                />
              </div>

              <div>
                <label
                  htmlFor="max_participants"
                  className="block text-sm font-medium text-gray-700"
                >
                  Maximum Participants (Optional)
                </label>
                <input
                  type="number"
                  name="max_participants"
                  id="max_participants"
                  min="1"
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                  value={formData.max_participants}
                  onChange={handleChange}
                  placeholder="Leave blank for unlimited participants"
                />
              </div>

              <div>
                <label
                  htmlFor="description"
                  className="block text-sm font-medium text-gray-700"
                >
                  Description
                </label>
                <textarea
                  name="description"
                  id="description"
                  rows={3}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-[#C4E538] focus:border-[#C4E538] sm:text-sm"
                  value={formData.description}
                  onChange={handleChange}
                  placeholder="Enter event description"
                />
              </div>

              {error && (
                <div className="text-red-600 text-sm">{error}</div>
              )}

              <div className="flex justify-end">
                <button
                  type="submit"
                  disabled={loading}
                  className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-gray-900 bg-[#C4E538] hover:bg-[#B4D532] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538] disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? 'Creating...' : 'Create Event'}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateEvent;
