import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getEvent, registerForEvent, cancelEvent } from '../services/api';
import { Event } from '../types';
import { format } from 'date-fns';
import { useAuth } from '../contexts/AuthContext';

const EventDetails: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [event, setEvent] = useState<Event | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [toast, setToast] = useState<{ message: string; type: 'success' | 'error' } | null>(null);

  useEffect(() => {
    fetchEvent();
  }, [id]);

  const fetchEvent = async () => {
    try {
      setLoading(true);
      const data = await getEvent(Number(id));
      setEvent(data);
      setError(null);
    } catch (err: any) {
      console.error('Failed to load event:', err);
      setError('Failed to load event details');
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async () => {
    if (!event) return;
    try {
      await registerForEvent(event.id);
      setToast({ message: 'Successfully registered for event!', type: 'success' });
      fetchEvent(); // Refresh event data
    } catch (err: any) {
      setToast({ 
        message: err.response?.data?.detail || 'Failed to register for event', 
        type: 'error' 
      });
    }
  };

  const handleWithdraw = async () => {
    if (!event) return;
    try {
      await registerForEvent(event.id, true);
      setToast({ message: 'Successfully withdrew from event', type: 'success' });
      fetchEvent(); // Refresh event data
    } catch (err: any) {
      setToast({ 
        message: err.response?.data?.detail || 'Failed to withdraw from event', 
        type: 'error' 
      });
    }
  };

  const handleCancelEvent = async () => {
    if (!event) return;
    try {
      await cancelEvent(event.id);
      setToast({ message: 'Successfully cancelled event', type: 'success' });
      navigate('/events');
    } catch (err: any) {
      setToast({ 
        message: err.response?.data?.detail || 'Failed to cancel event', 
        type: 'error' 
      });
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg text-gray-600">Loading event details...</div>
      </div>
    );
  }

  if (error || !event) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg text-red-600">{error || 'Event not found'}</div>
      </div>
    );
  }

  const isOrganizer = event.organizer_id === user?.id;
  const isRegistered = event.registrations?.some(reg => reg.user?.id === user?.id) || false;

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="px-6 py-8">
          <div className="flex justify-between items-start">
            <h1 className="text-3xl font-bold text-gray-900">
              Tennis Match at {event.court_location}
            </h1>
            {event.is_cancelled && (
              <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-red-100 text-red-800">
                Cancelled
              </span>
            )}
          </div>

          <div className="mt-6 space-y-6">
            <div>
              <h2 className="text-lg font-medium text-gray-900">Date & Time</h2>
              <p className="mt-1 text-gray-500">
                {format(new Date(event.event_date), 'MMMM d, yyyy')} at{' '}
                {format(new Date(event.event_time), 'h:mm a')}
              </p>
            </div>

            {event.description && (
              <div>
                <h2 className="text-lg font-medium text-gray-900">Description</h2>
                <p className="mt-1 text-gray-500">{event.description}</p>
              </div>
            )}

            <div>
              <h2 className="text-lg font-medium text-gray-900">Participants</h2>
              <p className="mt-1 text-gray-500">
                {event.registrations?.length || 0} / {event.max_participants || 'Unlimited'} spots filled
              </p>
              <div className="mt-4 space-y-2">
                {event.registrations?.map((registration) => (
                  <div key={registration.id} className="flex items-center space-x-3">
                    {registration.user?.profile_image ? (
                      <img
                        src={`http://localhost:8000/uploads/${registration.user.profile_image}`}
                        alt={`${registration.user?.first_name || ''} ${registration.user?.last_name || ''}`}
                        className="h-8 w-8 rounded-full"
                      />
                    ) : (
                      <div className="h-8 w-8 rounded-full bg-[#C4E538] flex items-center justify-center text-gray-900 font-medium">
                        {(registration.user?.first_name || 'U').charAt(0).toUpperCase()}
                      </div>
                    )}
                    <span className="text-gray-700">
                      {registration.user ? `${registration.user.first_name} ${registration.user.last_name}` : 'Unknown User'}
                      {registration.user.id === event.organizer_id && ' (Organizer)'}
                    </span>
                  </div>
                ))}
              </div>
            </div>

            <div className="flex justify-end space-x-4">
              <button
                onClick={() => navigate('/events')}
                className="px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538]"
              >
                Back to Events
              </button>
              {!event.is_cancelled && (
                <>
                  {isOrganizer ? (
                    <>
                      {isRegistered ? (
                        <button
                          onClick={handleWithdraw}
                          className="px-4 py-2 border border-transparent text-sm font-medium rounded-md text-yellow-700 bg-yellow-100 hover:bg-yellow-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-yellow-500"
                        >
                          Withdraw
                        </button>
                      ) : (
                        <button
                          onClick={handleRegister}
                          className="px-4 py-2 border border-transparent text-sm font-medium rounded-md text-gray-900 bg-[#C4E538] hover:bg-[#B4D532] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538]"
                        >
                          Register
                        </button>
                      )}
                      <button
                        onClick={handleCancelEvent}
                        className="px-4 py-2 border border-transparent text-sm font-medium rounded-md text-red-700 bg-red-100 hover:bg-red-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                      >
                        Cancel Event
                      </button>
                    </>
                  ) : (
                    <button
                      onClick={isRegistered ? handleWithdraw : handleRegister}
                      className={`px-4 py-2 border border-transparent text-sm font-medium rounded-md ${
                        isRegistered
                          ? 'text-yellow-700 bg-yellow-100 hover:bg-yellow-200'
                          : 'text-gray-900 bg-[#C4E538] hover:bg-[#B4D532]'
                      } focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-yellow-500`}
                    >
                      {isRegistered ? 'Withdraw' : 'Register'}
                    </button>
                  )}
                </>
              )}
            </div>
          </div>
        </div>
      </div>

      {toast && (
        <div className={`fixed top-4 right-4 z-50 p-4 rounded-md shadow-lg ${
          toast.type === 'success' ? 'bg-green-500' : 'bg-red-500'
        } text-white`}>
          {toast.message}
        </div>
      )}
    </div>
  );
};

export default EventDetails;
