import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import { Event, Registration } from '../types';

const EventDetails: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { user } = useAuth();
  const { showToast } = useToast();
  const [event, setEvent] = useState<Event | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const response = await fetch(`/api/events/${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch event details');
        }
        const data = await response.json();
        setEvent(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'An error occurred');
        showToast('Failed to load event details', 'error');
      } finally {
        setLoading(false);
      }
    };

    fetchEvent();
  }, [id, showToast]);

  const handleRegister = async () => {
    if (!user) {
      showToast('Please log in to register for events', 'warning');
      navigate('/login');
      return;
    }

    try {
      const response = await fetch(`/api/events/${id}/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Failed to register for event');
      }

      showToast('Successfully registered for event!', 'success');
      // Refresh event details to update registration status
      const updatedEvent = await response.json();
      setEvent(updatedEvent);
    } catch (err) {
      showToast('Failed to register for event', 'error');
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error || !event) {
    return <div>Error: {error || 'Event not found'}</div>;
  }

  const isRegistered = event.registrations?.some(
    (registration: Registration) => registration.user_id === user?.id
  );
  const isFull = (event.registrations?.length ?? 0) >= event.max_participants;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">{event.title}</h1>
      
      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <p className="text-gray-600 mb-4">{event.description}</p>
            
            <div className="space-y-4">
              <div>
                <h3 className="font-semibold">Date & Time</h3>
                <p>{new Date(event.date).toLocaleString()}</p>
              </div>
              
              <div>
                <h3 className="font-semibold">Location</h3>
                <p>{event.location}</p>
              </div>
              
              <div>
                <h3 className="font-semibold">Skill Level</h3>
                <p className="capitalize">{event.skill_level}</p>
              </div>
              
              <div>
                <h3 className="font-semibold">Event Type</h3>
                <p className="capitalize">{event.event_type}</p>
              </div>
              
              <div>
                <h3 className="font-semibold">Price</h3>
                <p>${event.price}</p>
              </div>
            </div>
          </div>
          
          <div className="flex flex-col justify-between">
            <div>
              <h3 className="font-semibold mb-2">Participants</h3>
              <p>
                {event.registrations?.length ?? 0} / {event.max_participants} spots filled
              </p>
            </div>
            
            {!isRegistered && !isFull && (
              <button
                onClick={handleRegister}
                className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition-colors"
              >
                Register Now
              </button>
            )}
            
            {isRegistered && (
              <p className="text-green-600 font-semibold">You are registered for this event</p>
            )}
            
            {isFull && !isRegistered && (
              <p className="text-red-600 font-semibold">This event is full</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetails;
