import React from 'react';

interface LocationMapProps {
  location: string;
}

const LocationMap: React.FC<LocationMapProps> = ({ location }) => {
  // Encode the location for the URL
  const encodedLocation = encodeURIComponent(location);
  
  return (
    <div className="h-48 w-full rounded-lg overflow-hidden">
      <a 
        href={`https://www.openstreetmap.org/search?query=${encodedLocation}`}
        target="_blank"
        rel="noopener noreferrer"
        className="block h-full w-full"
      >
        <img
          src={`https://staticmap.openstreetmap.de/staticmap.php?center=46.8182,8.2275&zoom=13&size=600x300&markers=46.8182,8.2275,red`}
          alt={`Map showing ${location}`}
          className="w-full h-full object-cover"
        />
      </a>
    </div>
  );
};

export default LocationMap;
