import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getMyRegistrations, cancelRegistration } from '../services/api';
import { EventRegistration, Event } from '../types';
import { useAuth } from '../contexts/AuthContext';

const MyRegistrations: React.FC = () => {
  const navigate = useNavigate();
  const { user, isAuthenticated } = useAuth();
  const [registrations, setRegistrations] = useState<EventRegistration[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [toast, setToast] = useState<{ message: string; type: 'success' | 'error' } | null>(null);
  const [selectedEvent, setSelectedEvent] = useState<Event | null>(null);
  const [expandedSections, setExpandedSections] = useState({
    upcoming: true,
    past: false
  });

  useEffect(() => {
    console.log('MyRegistrations component mounted');
    console.log('Auth state:', { isAuthenticated, user });
    fetchRegistrations();
  }, []);

  useEffect(() => {
    if (toast) {
      const timer = setTimeout(() => {
        setToast(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [toast]);

  const fetchRegistrations = async () => {
    try {
      console.log('Fetching registrations...');
      const token = localStorage.getItem('token');
      console.log('Auth token present:', !!token);
      const data = await getMyRegistrations();
      console.log('Received registrations data:', data);
      if (!data || data.length === 0) {
        console.log('No registrations found');
      } else {
        console.log('Number of registrations:', data.length);
        data.forEach((reg, index) => {
          console.log(`Registration ${index + 1}:`, {
            id: reg.id,
            eventId: reg.event?.id,
            event: reg.event ? {
              id: reg.event.id,
              location: reg.event.court_location,
              date: reg.event.event_date
            } : 'No event data'
          });
        });
      }
      setRegistrations(data);
      console.log('Registrations state after update:', data);
    } catch (err: any) {
      console.error('Error fetching registrations:', err);
      console.error('Error details:', {
        message: err.message,
        response: err.response?.data,
        status: err.response?.status
      });
      setError('Failed to load registrations');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelRegistration = async (registrationId: number) => {
    try {
      await cancelRegistration(registrationId);
      setRegistrations(registrations.filter(reg => reg.id !== registrationId));
      setToast({ message: 'Successfully withdrew from event', type: 'success' });
    } catch (err: any) {
      console.error('Error canceling registration:', err);
      let errorMsg = 'Failed to withdraw from event';
      if (err.response?.data?.detail) {
        errorMsg = typeof err.response.data.detail === 'string' 
          ? err.response.data.detail 
          : JSON.stringify(err.response.data.detail);
      } else if (err.message) {
        errorMsg = err.message;
      }
      setToast({ message: errorMsg, type: 'error' });
    }
  };

  const toggleSection = (section: 'upcoming' | 'past') => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const categorizeRegistrations = () => {
    console.log('Categorizing registrations:', registrations);
    const now = new Date();
    const result = registrations.reduce((acc, registration) => {
      if (!registration.event) {
        console.log('Registration without event:', registration);
        return acc;
      }
      const eventDate = new Date(registration.event.event_date);
      if (eventDate >= now) {
        acc.upcoming.push(registration);
      } else {
        acc.past.push(registration);
      }
      return acc;
    }, { upcoming: [] as EventRegistration[], past: [] as EventRegistration[] });
    console.log('Categorized registrations:', result);
    return result;
  };

  const renderRegistrationSection = (
    title: string,
    registrations: EventRegistration[],
    section: 'upcoming' | 'past'
  ) => {
    console.log(`Rendering ${section} section with ${registrations.length} registrations`);
    if (registrations.length === 0) {
      return null;
    }

    return (
      <div className="mb-8">
        <div
          className="flex items-center justify-between cursor-pointer mb-4"
          onClick={() => toggleSection(section)}
        >
          <h2 className="text-xl font-semibold text-gray-900">{title}</h2>
          <button
            className="text-gray-500 hover:text-gray-700 focus:outline-none"
            onClick={(e) => {
              e.stopPropagation();
              toggleSection(section);
            }}
          >
            {expandedSections[section] ? (
              <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            ) : (
              <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            )}
          </button>
        </div>
        {expandedSections[section] && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {registrations.map((registration) => (
              <div
                key={registration.id}
                className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300"
              >
                <div className="p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">
                    {registration.event.court_location}
                  </h3>
                  <p className="text-gray-600 mb-4">
                    {new Date(registration.event.event_date).toLocaleDateString()} at{' '}
                    {new Date(registration.event.event_time).toLocaleTimeString([], {
                      hour: '2-digit',
                      minute: '2-digit',
                    })}
                  </p>
                  {registration.event.description && (
                    <p className="text-gray-600 mb-4">{registration.event.description}</p>
                  )}
                  <div className="flex justify-between items-center mb-4">
                    <span className="text-sm text-gray-500">
                      {registration.event.registrations?.length || 0}
                      {registration.event.max_participants ? `/${registration.event.max_participants}` : ''} participants
                    </span>
                  </div>

                  <div className="mt-6 space-y-3">
                    <button
                      onClick={() => setSelectedEvent(registration.event)}
                      className="w-full inline-flex justify-center items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538]"
                    >
                      See Event Details
                    </button>
                    {section === 'upcoming' && (
                      <button
                        onClick={() => handleCancelRegistration(registration.id)}
                        className="w-full inline-flex justify-center items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                      >
                        Withdraw
                      </button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-[#C4E538] mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading your registrations...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center">
            <p className="text-red-600">{error}</p>
            <button
              onClick={fetchRegistrations}
              className="mt-4 text-[#C4E538] hover:text-[#B4D532]"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  const { upcoming, past } = categorizeRegistrations();

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      {/* Toast Notification */}
      {toast && (
        <div className={`fixed top-4 right-4 z-50 p-4 rounded-md shadow-lg ${
          toast.type === 'success' ? 'bg-green-500' : 'bg-red-500'
        } text-white`}>
          {toast.message}
        </div>
      )}

      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900">My Registrations</h1>
          <p className="mt-2 text-gray-600">
            View and manage your event registrations
          </p>
        </div>

        {registrations.length === 0 ? (
          <div className="text-center">
            <p className="text-gray-600">You haven't registered for any events yet.</p>
            <button
              onClick={() => navigate('/dashboard')}
              className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-gray-900 bg-[#C4E538] hover:bg-[#B4D532] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538]"
            >
              Find Events
            </button>
          </div>
        ) : (
          <>
            {renderRegistrationSection('Upcoming Events', upcoming, 'upcoming')}
            {renderRegistrationSection('Past Events', past, 'past')}
          </>
        )}
      </div>

      {/* Event Details Modal */}
      {selectedEvent && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-start">
                <h2 className="text-2xl font-bold text-gray-900">
                  Event Details
                </h2>
                <button
                  onClick={() => setSelectedEvent(null)}
                  className="text-gray-400 hover:text-gray-500"
                >
                  <span className="sr-only">Close</span>
                  <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              <div className="mt-4 space-y-4">
                <div>
                  <h3 className="text-lg font-medium text-gray-900">Location</h3>
                  <p className="mt-1 text-gray-600">{selectedEvent.court_location}</p>
                </div>
                <div>
                  <h3 className="text-lg font-medium text-gray-900">Date & Time</h3>
                  <p className="mt-1 text-gray-600">
                    {new Date(selectedEvent.event_date).toLocaleDateString()} at{' '}
                    {new Date(selectedEvent.event_time).toLocaleTimeString([], {
                      hour: '2-digit',
                      minute: '2-digit',
                    })}
                  </p>
                </div>
                {selectedEvent.description && (
                  <div>
                    <h3 className="text-lg font-medium text-gray-900">Description</h3>
                    <p className="mt-1 text-gray-600">{selectedEvent.description}</p>
                  </div>
                )}
                <div>
                  <h3 className="text-lg font-medium text-gray-900">Participants</h3>
                  <p className="mt-1 text-gray-600">
                    {selectedEvent.registrations?.length || 0}
                    {selectedEvent.max_participants ? `/${selectedEvent.max_participants}` : ''} participants
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MyRegistrations;
