import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getMyRegistrations, registerForEvent } from '../services/api';
import { EventRegistration, Event } from '../types';
import { useAuth } from '../contexts/AuthContext';

interface Section {
  active: boolean;
  withdrawn: boolean;
  cancelled: boolean;
}

const MyRegistrations: React.FC = () => {
  const [registrations, setRegistrations] = useState<EventRegistration[]>([]);
  const [withdrawnRegistrations, setWithdrawnRegistrations] = useState<EventRegistration[]>([]);
  const [cancelledRegistrations, setCancelledRegistrations] = useState<EventRegistration[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [toast, setToast] = useState<{ message: string; type: 'success' | 'error' } | null>(null);
  const [expandedSections, setExpandedSections] = useState<Section>({
    active: true,
    withdrawn: false,
    cancelled: false
  });
  const navigate = useNavigate();
  const { user } = useAuth();

  const fetchRegistrations = async () => {
    try {
      setLoading(true);
      console.log('Fetching registrations...');
      const data = await getMyRegistrations();
      console.log('Fetched registrations:', data);

      // Separate registrations into active, withdrawn, and cancelled
      const active = data.filter(reg => !reg.event.is_cancelled && reg.status === "active");
      const withdrawn = data.filter(reg => !reg.event.is_cancelled && reg.status === "withdrawn");
      const cancelled = data.filter(reg => reg.event.is_cancelled);

      console.log('Filtered registrations:', {
        active: active.length,
        withdrawn: withdrawn.length,
        cancelled: cancelled.length
      });

      setRegistrations(active);
      setWithdrawnRegistrations(withdrawn);
      setCancelledRegistrations(cancelled);
    } catch (error) {
      console.error('Error fetching registrations:', error);
      setToast({ 
        message: 'Failed to fetch registrations', 
        type: 'error' 
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRegistrations();
  }, []);

  const handleCancelRegistration = async (registrationId: number) => {
    try {
      setLoading(true);
      console.log('Attempting to cancel registration:', registrationId);
      // Get the event_id from the registration
      const registration = registrations.find(reg => reg.id === registrationId);
      if (!registration) {
        throw new Error('Registration not found');
      }
      console.log('Found registration:', registration);
      console.log('Calling registerForEvent with:', { eventId: registration.event_id, isWithdraw: true });
      const response = await registerForEvent(registration.event_id, true);
      console.log('Withdrawal response:', response);
      setToast({ message: 'Successfully withdrew from event', type: 'success' });

      // Refresh all registrations
      console.log('Refreshing registrations...');
      await fetchRegistrations();
      console.log('Registrations refreshed');
    } catch (error: any) {
      console.error('Failed to withdraw from event:', error);
      console.error('Error details:', {
        status: error.response?.status,
        data: error.response?.data,
        message: error.message,
        detail: error.detail
      });
      setToast({ 
        message: error.response?.data?.detail || error.detail || 'Failed to withdraw from event', 
        type: 'error' 
      });
    } finally {
      setLoading(false);
    }
  };

  const handleRegisterAgain = async (registrationId: number) => {
    try {
      setLoading(true);
      console.log('Attempting to register again:', registrationId);
      const registration = withdrawnRegistrations.find(reg => reg.id === registrationId);
      if (!registration) {
        throw new Error('Registration not found');
      }
      await registerForEvent(registration.event_id, false);
      console.log('Successfully registered again');
      setToast({ message: 'Successfully registered for event', type: 'success' });

      // Refresh all registrations
      await fetchRegistrations();
    } catch (error: any) {
      console.error('Failed to register again:', error);
      console.error('Error details:', {
        status: error.response?.status,
        data: error.response?.data,
        message: error.message,
        detail: error.detail
      });
      setToast({ 
        message: error.response?.data?.detail || error.detail || 'Failed to register again', 
        type: 'error' 
      });
    } finally {
      setLoading(false);
    }
  };

  const toggleSection = (section: keyof Section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const renderRegistrationCard = (registration: EventRegistration, isCancelled: boolean = false, isWithdrawn: boolean = false) => (
    <div
      key={registration.id}
      className={`bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300 ${isCancelled ? 'opacity-75' : ''}`}
    >
      <div className="p-6">
        <h3 className="text-lg font-medium text-gray-900">
          Tennis Match at {registration.event.court_location}
        </h3>
        <div className="mt-2 text-sm text-gray-500">
          <p>
            Date:{' '}
            {new Date(registration.event.event_date).toLocaleDateString('en-US', {
              month: 'long',
              day: 'numeric',
              year: 'numeric'
            })}
          </p>
          <p>
            Time:{' '}
            {new Date(registration.event.event_time).toLocaleTimeString([], {
              hour: 'numeric',
              minute: '2-digit',
              hour12: true
            })}
          </p>
          {registration.event.max_participants ? (
            <p>
              Participants: {registration.event.registrations?.length || 0}/{registration.event.max_participants}
            </p>
          ) : (
            <p>
              Participants: Open Registration
            </p>
          )}
          {isCancelled && (
            <p className="text-red-600 mt-2">This event has been cancelled</p>
          )}
          {isWithdrawn && (
            <p className="text-yellow-600 mt-2">You have withdrawn from this event</p>
          )}
        </div>
        <div className="mt-6 space-y-3">
          <button
            onClick={() => navigate(`/events/${registration.event.id}`)}
            className="w-full inline-flex justify-center items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538]"
          >
            See Event Details
          </button>
          {!isCancelled && !isWithdrawn && (
            <button
              onClick={() => handleCancelRegistration(registration.id)}
              className="w-full inline-flex justify-center items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-yellow-700 bg-yellow-100 hover:bg-yellow-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-yellow-500"
            >
              Withdraw
            </button>
          )}
          {isWithdrawn && (
            <button
              onClick={() => handleRegisterAgain(registration.id)}
              className="w-full inline-flex justify-center items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-[#C4E538] hover:bg-[#B4D532] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538]"
            >
              Register Again
            </button>
          )}
        </div>
      </div>
    </div>
  );

  if (loading && registrations.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-[#C4E538] mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading your registrations...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      {/* Toast Notification */}
      {toast && (
        <div className={`fixed top-4 right-4 z-50 p-4 rounded-md shadow-lg ${
          toast.type === 'success' ? 'bg-green-500' : 'bg-red-500'
        } text-white`}>
          {toast.message}
        </div>
      )}

      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900">My Registrations</h1>
          <p className="mt-2 text-gray-600">
            View and manage your event registrations
          </p>
        </div>

        {/* Active Registrations Section */}
        <div className="mb-8">
          <div className="flex items-center justify-between cursor-pointer mb-4">
            <h2 className="text-xl font-semibold text-gray-900">Active Registrations</h2>
            <button
              className="text-gray-500 hover:text-gray-700 focus:outline-none"
              onClick={() => toggleSection('active')}
            >
              {expandedSections.active ? '▼' : '▶'}
            </button>
          </div>
          {expandedSections.active && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {registrations.length === 0 ? (
                <p className="text-gray-500 col-span-full text-center">No active registrations</p>
              ) : (
                registrations.map(registration => renderRegistrationCard(registration))
              )}
            </div>
          )}
        </div>

        {/* Withdrawn Registrations Section */}
        <div className="mb-8">
          <div className="flex items-center justify-between cursor-pointer mb-4">
            <h2 className="text-xl font-semibold text-gray-900">Withdrawn Registrations</h2>
            <button
              className="text-gray-500 hover:text-gray-700 focus:outline-none"
              onClick={() => toggleSection('withdrawn')}
            >
              {expandedSections.withdrawn ? '▼' : '▶'}
            </button>
          </div>
          {expandedSections.withdrawn && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {withdrawnRegistrations.length === 0 ? (
                <p className="text-gray-500 col-span-full text-center">No withdrawn registrations</p>
              ) : (
                withdrawnRegistrations.map(registration => renderRegistrationCard(registration, false, true))
              )}
            </div>
          )}
        </div>

        {/* Cancelled Events Section */}
        <div>
          <div className="flex items-center justify-between cursor-pointer mb-4">
            <h2 className="text-xl font-semibold text-gray-900">Cancelled Events</h2>
            <button
              className="text-gray-500 hover:text-gray-700 focus:outline-none"
              onClick={() => toggleSection('cancelled')}
            >
              {expandedSections.cancelled ? '▼' : '▶'}
            </button>
          </div>
          {expandedSections.cancelled && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {cancelledRegistrations.length === 0 ? (
                <p className="text-gray-500 col-span-full text-center">No cancelled events</p>
              ) : (
                cancelledRegistrations.map(registration => renderRegistrationCard(registration, true))
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MyRegistrations;
