import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getEvents, registerForEvent } from '../services/api';
import { Event } from '../types';
import { format, isThisWeek, isThisMonth, addWeeks, startOfWeek, endOfWeek, isPast, parseISO } from 'date-fns';
import { useAuth } from '../contexts/AuthContext';

const Dashboard: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();
  const [toast, setToast] = useState<{ message: string; type: 'success' | 'error' } | null>(null);
  const [expandedSections, setExpandedSections] = useState({
    thisWeek: true,
    nextWeek: true,
    thisMonth: true,
    pastEvents: false
  });

  useEffect(() => {
    fetchEvents();
  }, []);

  // Auto-hide toast after 3 seconds
  useEffect(() => {
    if (toast) {
      const timer = setTimeout(() => {
        setToast(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [toast]);

  const fetchEvents = async () => {
    try {
      const data = await getEvents();
      // Sort events by date
      const sortedEvents = data.sort((a, b) => 
        new Date(a.event_date).getTime() - new Date(b.event_date).getTime()
      );
      setEvents(sortedEvents);
      setError(null);
    } catch (err: any) {
      console.error('Failed to load events:', err);
      let errorMsg = 'Failed to load events';
      if (err.detail) {
        errorMsg = typeof err.detail === 'string' ? err.detail : JSON.stringify(err.detail);
      } else if (err.message) {
        errorMsg = err.message;
      }
      setError(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (eventId: number) => {
    try {
      console.log('Attempting to register for event:', eventId);
      await registerForEvent(eventId);
      // Refresh events after registration
      fetchEvents();
      setToast({ message: 'Successfully registered for event!', type: 'success' });
    } catch (err: any) {
      console.error('Failed to register for event:', err);
      console.error('Error details:', {
        status: err.response?.status,
        data: err.response?.data,
        message: err.message,
        detail: err.detail
      });
      let errorMsg = 'Failed to register for event';
      if (err.detail) {
        errorMsg = typeof err.detail === 'string' ? err.detail : JSON.stringify(err.detail);
      } else if (err.message) {
        errorMsg = err.message;
      }
      setError(errorMsg);
      setToast({ message: errorMsg, type: 'error' });
    }
  };

  const handleWithdraw = async (eventId: number) => {
    try {
      console.log('Attempting to withdraw from event:', eventId);
      const response = await registerForEvent(eventId, true);
      console.log('Withdrawal response:', response);
      setToast({ message: 'Successfully withdrew from event', type: 'success' });
      fetchEvents(); // Refresh events to update the UI
    } catch (error: any) {
      console.error('Failed to withdraw from event:', error);
      console.error('Error details:', {
        status: error.response?.status,
        data: error.response?.data,
        message: error.message,
        detail: error.detail
      });
      setToast({ 
        message: error.response?.data?.detail || error.detail || 'Failed to withdraw from event', 
        type: 'error' 
      });
    }
  };

  const categorizeEvents = () => {
    const now = new Date();
    const nextWeekStart = startOfWeek(addWeeks(now, 1), { weekStartsOn: 1 });
    const nextWeekEnd = endOfWeek(addWeeks(now, 1), { weekStartsOn: 1 });

    const thisWeekEvents = events.filter(event => 
      isThisWeek(new Date(event.event_date), { weekStartsOn: 1 })
    );
    const nextWeekEvents = events.filter(event => {
      const eventDate = new Date(event.event_date);
      return eventDate >= nextWeekStart && eventDate <= nextWeekEnd;
    });
    const thisMonthEvents = events.filter(event => 
      isThisMonth(new Date(event.event_date)) && 
      !isThisWeek(new Date(event.event_date), { weekStartsOn: 1 }) &&
      !(new Date(event.event_date) >= nextWeekStart && new Date(event.event_date) <= nextWeekEnd)
    );
    const pastEvents = events
      .filter(event => isPast(parseISO(event.event_date)))
      .sort((a, b) => new Date(b.event_date).getTime() - new Date(a.event_date).getTime())
      .slice(0, 10);

    return {
      thisWeek: thisWeekEvents,
      nextWeek: nextWeekEvents,
      thisMonth: thisMonthEvents,
      pastEvents: pastEvents
    };
  };

  const toggleSection = (section: keyof typeof expandedSections) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const renderEventCard = (event: Event) => {
    const totalSpots = event.max_participants || null;
    const currentRegistrations = event.registrations?.length || 0;
    // Subtract 1 from available spots because organizer is automatically registered
    const availableSpots = totalSpots ? totalSpots - currentRegistrations : null;

    console.log('Event ID:', event.id);
    console.log('Current user ID:', user?.id);
    console.log('Event registrations:', event.registrations);

    // Check if current user is registered for this event
    const isRegistered = event.registrations?.some(reg => reg.user_id === user?.id) || false;
    console.log('Is registered:', isRegistered);

    return (
      <div
        key={event.id}
        className="bg-white overflow-hidden shadow rounded-lg hover:shadow-md transition-shadow duration-200"
      >
        <div className="px-4 py-5 sm:p-6">
          <h3 className="text-lg font-medium text-gray-900">
            Tennis Match at {event.court_location}
          </h3>
          <div className="mt-2 text-sm text-gray-500">
            <p>
              Date:{' '}
              {format(new Date(event.event_date), 'MMMM d, yyyy')}
            </p>
            <p>
              Time:{' '}
              {format(new Date(event.event_time), 'h:mm a')}
            </p>
            {totalSpots !== null ? (
              <p>
                Spots:{' '}
                {Math.max(0, availableSpots || 0)}/{totalSpots}
              </p>
            ) : (
              <p>
                Spots: Open Registration
              </p>
            )}
          </div>

          <div className="mt-6">
            <button
              onClick={() => isRegistered ? handleWithdraw(event.id) : handleRegister(event.id)}
              disabled={event.is_cancelled}
              className={`w-full inline-flex justify-center items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white ${
                event.is_cancelled
                  ? 'bg-gray-400 cursor-not-allowed'
                  : isRegistered
                  ? 'bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500'
                  : 'bg-[#C4E538] hover:bg-[#B4D532] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#C4E538] text-gray-900'
              }`}
            >
              {event.is_cancelled
                ? 'Event Cancelled'
                : isRegistered
                ? 'Withdraw'
                : totalSpots !== null && (availableSpots || 0) <= 0
                ? 'Event Full'
                : 'Register for Event'}
            </button>
          </div>
        </div>
      </div>
    );
  };

  const renderEventSection = (title: string, events: Event[], sectionKey: keyof typeof expandedSections) => (
    <div className="mb-8">
      <button
        onClick={() => toggleSection(sectionKey)}
        className="w-full flex justify-between items-center text-2xl font-bold text-gray-900 mb-4 hover:text-[#C4E538] transition-colors"
      >
        <span>{title}</span>
        <span className="text-lg">
          {expandedSections[sectionKey] ? '▼' : '▶'}
        </span>
      </button>
      {expandedSections[sectionKey] && events.length > 0 ? (
        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {events.map(renderEventCard)}
        </div>
      ) : (
        <p className="text-gray-500 text-center py-4">
          {expandedSections[sectionKey] ? 'No events in this period' : ''}
        </p>
      )}
    </div>
  );

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg text-gray-600">Loading events...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg text-red-600">{error}</div>
      </div>
    );
  }

  const categorizedEvents = categorizeEvents();
  const hasAnyEvents = events.length > 0;

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      {/* Toast Notification */}
      {toast && (
        <div className={`fixed top-4 right-4 z-50 p-4 rounded-md shadow-lg ${
          toast.type === 'success' ? 'bg-green-500' : 'bg-red-500'
        } text-white`}>
          {toast.message}
        </div>
      )}

      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Upcoming Events</h1>
      </div>

      {hasAnyEvents ? (
        <div>
          {renderEventSection('This Week', categorizedEvents.thisWeek, 'thisWeek')}
          {renderEventSection('Next Week', categorizedEvents.nextWeek, 'nextWeek')}
          {renderEventSection('This Month', categorizedEvents.thisMonth, 'thisMonth')}
          {renderEventSection('Past Events', categorizedEvents.pastEvents, 'pastEvents')}
        </div>
      ) : (
        <div className="text-center py-12">
          <div className="inline-flex items-center justify-center w-24 h-24 rounded-full bg-gray-100 mb-4">
            <svg
              className="w-12 h-12 text-gray-400"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10"
              />
            </svg>
          </div>
          <h3 className="text-lg font-medium text-gray-900">
            No events available
          </h3>
          <p className="mt-2 text-sm text-gray-500">
            Be the first to create an event!
          </p>
          <div className="mt-6">
            <Link
              to="/create-event"
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
            >
              Create Your First Event
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
