import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import { Event, Registration } from '../types/event';

const EventDetails: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { user } = useAuth();
  const { showToast } = useToast();
  const [event, setEvent] = useState<Event | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/events/${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch event');
        }
        const data = await response.json();
        setEvent(data);
      } catch (error) {
        showToast('Failed to load event details', 'error');
        navigate('/events');
      } finally {
        setLoading(false);
      }
    };

    fetchEvent();
  }, [id, navigate, showToast]);

  const handleRegister = async () => {
    if (!user) {
      showToast('Please log in to register for events', 'warning');
      navigate('/login');
      return;
    }

    try {
      const response = await fetch(`http://localhost:8000/api/events/${id}/register`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to register for event');
      }

      showToast('Successfully registered for event!', 'success');
      // Refresh event data
      const updatedEvent = await response.json();
      setEvent(updatedEvent);
    } catch (error) {
      showToast('Failed to register for event', 'error');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-[#C4E538]"></div>
      </div>
    );
  }

  if (!event) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-gray-900">Event not found</h2>
        <button
          onClick={() => navigate('/events')}
          className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-[#C4E538] hover:bg-[#B4D532]"
        >
          Back to Events
        </button>
      </div>
    );
  }

  const isRegistered = event.registrations?.some((reg: Registration) => reg.user_id === user?.id);
  const isFull = (event.registrations?.length ?? 0) >= event.maxParticipants;

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="p-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">{event.title}</h1>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            <div>
              <h2 className="text-lg font-semibold text-gray-900 mb-2">Event Details</h2>
              <div className="space-y-2">
                <p className="text-gray-600">
                  <span className="font-medium">Date:</span>{' '}
                  {new Date(event.date).toLocaleDateString()}
                </p>
                <p className="text-gray-600">
                  <span className="font-medium">Time:</span>{' '}
                  {new Date(event.date).toLocaleTimeString()}
                </p>
                <p className="text-gray-600">
                  <span className="font-medium">Location:</span> {event.location}
                </p>
                <p className="text-gray-600">
                  <span className="font-medium">Skill Level:</span> {event.skillLevel}
                </p>
                <p className="text-gray-600">
                  <span className="font-medium">Event Type:</span> {event.eventType}
                </p>
                <p className="text-gray-600">
                  <span className="font-medium">Price:</span> ${event.price}
                </p>
              </div>
            </div>

            <div>
              <h2 className="text-lg font-semibold text-gray-900 mb-2">Description</h2>
              <p className="text-gray-600 whitespace-pre-wrap">{event.description}</p>
            </div>
          </div>

          <div className="border-t border-gray-200 pt-6">
            <div className="flex justify-between items-center">
              <div>
                <p className="text-gray-600">
                  <span className="font-medium">Participants:</span>{' '}
                  {event.registrations?.length || 0} / {event.maxParticipants}
                </p>
              </div>
              {user && (
                <button
                  onClick={handleRegister}
                  disabled={isRegistered || isFull}
                  className={`inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white ${
                    isRegistered
                      ? 'bg-gray-400 cursor-not-allowed'
                      : isFull
                      ? 'bg-red-500 cursor-not-allowed'
                      : 'bg-[#C4E538] hover:bg-[#B4D532]'
                  }`}
                >
                  {isRegistered
                    ? 'Already Registered'
                    : isFull
                    ? 'Event Full'
                    : 'Register Now'}
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetails;
