import axios from 'axios';
import { AuthResponse, Event, EventRegistration, LoginCredentials, RegisterData, User, Sex, TennisLevel } from '../types';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor for better error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    console.error('API Error:', {
      status: error.response?.status,
      data: error.response?.data,
      message: error.message
    });
    
    if (!error.response) {
      // Network error or server not responding
      return Promise.reject({
        message: 'Unable to connect to server. Please check your internet connection.'
      });
    }
    
    if (error.response.status === 401) {
      // Clear token and redirect to login on auth error
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    
    return Promise.reject(error.response.data || error);
  }
);

const getToken = () => {
  return localStorage.getItem('token');
};

// Auth endpoints
export const login = async (username: string, password: string): Promise<AuthResponse> => {
  try {
    const formData = new URLSearchParams();
    formData.append('username', username);
    formData.append('password', password);

    const response = await api.post('/api/auth/token', formData, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
    });
    
    const { access_token } = response.data;
    localStorage.setItem('token', access_token);
    return response.data;
  } catch (error: any) {
    console.error('Login error:', error);
    throw error;
  }
};

export const register = async (userData: {
  email: string;
  password: string;
  first_name: string;
  last_name: string;
  date_of_birth: string;
  sex: Sex;
  tennis_level: TennisLevel;
}): Promise<User> => {
  try {
    const response = await api.post('/api/auth/register', userData);
    return response.data;
  } catch (error: any) {
    console.error('Registration error:', error);
    throw error;
  }
};

// User endpoints
export const getCurrentUser = async (): Promise<User> => {
  const response = await api.get<User>('/api/users/me');
  return response.data;
};

export const updateUser = async (data: Partial<User>): Promise<User> => {
  const response = await api.put<User>('/api/users/me', data);
  return response.data;
};

// Event endpoints
export const getEvents = async (): Promise<Event[]> => {
  try {
    const response = await api.get<Event[]>('/api/events');
    return response.data;
  } catch (error: any) {
    console.error('Get events error:', error);
    throw error;
  }
};

export const getMyEvents = async (): Promise<Event[]> => {
  try {
    const response = await api.get<Event[]>('/api/events/my-events');
    return response.data;
  } catch (error: any) {
    console.error('Get my events error:', error);
    throw error;
  }
};

export const createEvent = async (eventData: {
  court_location: string;
  event_date: string;
  event_time: string;
  max_participants: number;
}): Promise<Event> => {
  try {
    const response = await api.post('/api/events', eventData);
    return response.data;
  } catch (error: any) {
    console.error('Create event error:', error);
    throw error;
  }
};

export const registerForEvent = async (eventId: number): Promise<EventRegistration> => {
  try {
    const response = await api.post<EventRegistration>(`/api/events/${eventId}/register`);
    return response.data;
  } catch (error: any) {
    console.error('Register for event error:', error);
    throw error;
  }
};

export const cancelEvent = async (eventId: number): Promise<void> => {
  try {
    const response = await api.delete(`/api/events/${eventId}`);
    return response.data;
  } catch (error: any) {
    console.error('Cancel event error:', error);
    throw error;
  }
};

export default api;
