import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getMyRegistrations, cancelRegistration } from '../services/api';
import { EventRegistration, Event } from '../types';
import { useAuth } from '../contexts/AuthContext';
import { Toast } from '../components/Toast';

interface Section {
  active: boolean;
  withdrawn: boolean;
}

const MyRegistrations: React.FC = () => {
  const [registrations, setRegistrations] = useState<EventRegistration[]>([]);
  const [withdrawnRegistrations, setWithdrawnRegistrations] = useState<EventRegistration[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [toast, setToast] = useState<{ message: string; type: 'success' | 'error' } | null>(null);
  const [expandedSections, setExpandedSections] = useState<Section>({
    active: true,
    withdrawn: false
  });
  const navigate = useNavigate();
  const { user } = useAuth();

  const fetchRegistrations = async () => {
    try {
      setLoading(true);
      console.log('Fetching registrations...');
      const data = await getMyRegistrations();
      console.log('Fetched registrations:', data);
      setRegistrations(data);
    } catch (error) {
      console.error('Error fetching registrations:', error);
      setToast({ 
        message: 'Failed to fetch registrations', 
        type: 'error' 
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRegistrations();
  }, []);

  const handleCancelRegistration = async (registrationId: number) => {
    try {
      setLoading(true);
      console.log('Attempting to cancel registration:', registrationId);
      await cancelRegistration(registrationId);
      console.log('Successfully canceled registration');
      setToast({ message: 'Successfully withdrew from event', type: 'success' });

      // Find the registration to move
      const registrationToMove = registrations.find(reg => reg.id === registrationId);
      if (registrationToMove) {
        // Remove from active registrations
        setRegistrations(prevRegistrations => 
          prevRegistrations.filter(reg => reg.id !== registrationId)
        );
        // Add to withdrawn registrations
        setWithdrawnRegistrations(prev => [...prev, registrationToMove]);
      }
    } catch (error: any) {
      console.error('Failed to withdraw from event:', error);
      console.error('Error details:', {
        status: error.response?.status,
        data: error.response?.data,
        message: error.message,
        detail: error.detail
      });
      setToast({ 
        message: error.response?.data?.detail || error.detail || 'Failed to withdraw from event', 
        type: 'error' 
      });
    } finally {
      setLoading(false);
    }
  };

  const toggleSection = (section: keyof Section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const renderRegistrationCard = (registration: EventRegistration, isWithdrawn: boolean = false) => (
    <div key={registration.id} className="bg-white shadow rounded-lg overflow-hidden">
      <div className="p-6">
        <h3 className="text-lg font-medium text-gray-900">
          Tennis Match at {registration.event.court_location}
        </h3>
        <div className="mt-2 text-sm text-gray-500">
          <p>
            Date:{' '}
            {new Date(registration.event.event_date).toLocaleDateString('en-US', {
              month: 'long',
              day: 'numeric',
              year: 'numeric'
            })}
          </p>
          <p>
            Time:{' '}
            {new Date(registration.event.event_time).toLocaleTimeString([], {
              hour: 'numeric',
              minute: '2-digit',
              hour12: true
            })}
          </p>
          {registration.event.max_participants ? (
            <p>
              Spots:{' '}
              {registration.event.available_spots}/{registration.event.max_participants}
            </p>
          ) : (
            <p>
              Spots: Open Registration
            </p>
          )}
        </div>
        <div className="mt-4 flex justify-center space-x-2">
          <button
            onClick={() => navigate(`/events/${registration.event.id}`)}
            className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-gray-700 bg-gray-100 hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
          >
            See Event Details
          </button>
          {!isWithdrawn && (
            <button
              onClick={() => handleCancelRegistration(registration.id)}
              className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-yellow-700 bg-yellow-100 hover:bg-yellow-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-yellow-500"
            >
              Withdraw
            </button>
          )}
        </div>
      </div>
    </div>
  );

  if (loading && registrations.length === 0) {
    return (
      <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
        <div className="relative py-3 sm:max-w-xl sm:mx-auto">
          <div className="text-center">Loading...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto w-full px-4 sm:px-0">
        <div className="relative px-4 py-10 bg-white mx-8 md:mx-0 shadow rounded-3xl sm:p-10">
          <div className="max-w-md mx-auto">
            <div className="divide-y divide-gray-200">
              <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                <h2 className="text-2xl font-bold mb-4">My Registrations</h2>

                {/* Active Registrations Section */}
                <div className="mb-8">
                  <button
                    onClick={() => toggleSection('active')}
                    className="w-full flex justify-between items-center text-left font-semibold text-lg mb-2"
                  >
                    <span>Active Registrations</span>
                    <span>{expandedSections.active ? '▼' : '▶'}</span>
                  </button>
                  {expandedSections.active && (
                    <div className="space-y-4">
                      {registrations.length === 0 ? (
                        <p className="text-gray-500">No active registrations</p>
                      ) : (
                        registrations.map(registration => renderRegistrationCard(registration))
                      )}
                    </div>
                  )}
                </div>

                {/* Withdrawn Registrations Section */}
                <div>
                  <button
                    onClick={() => toggleSection('withdrawn')}
                    className="w-full flex justify-between items-center text-left font-semibold text-lg mb-2"
                  >
                    <span>Withdrawn Registrations</span>
                    <span>{expandedSections.withdrawn ? '▼' : '▶'}</span>
                  </button>
                  {expandedSections.withdrawn && (
                    <div className="space-y-4">
                      {withdrawnRegistrations.length === 0 ? (
                        <p className="text-gray-500">No withdrawn registrations</p>
                      ) : (
                        withdrawnRegistrations.map(registration => renderRegistrationCard(registration, true))
                      )}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      {toast && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={() => setToast(null)}
        />
      )}
    </div>
  );
};

export default MyRegistrations;
